// Выполнение HTTP запроса к API MoySklad
func fetchFromMoySklad(token, endpoint string) ([]byte, error) {
	req, err := http.NewRequest("GET", endpoint, nil)
	if err != nil {
		return nil, err
	}

	req.Header.Add("Authorization", "Bearer "+token)
	req.Header.Add("Content-Type", "application/json")

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		body, _ := ioutil.ReadAll(resp.Body)
		return nil, fmt.Errorf("неправильный статус ответа: %d, тело ответа: %s", resp.StatusCode, string(body))
	}

	return ioutil.ReadAll(resp.Body)
}

// Получение данных из MoySklad с кэшированием
func getDataFromMoySklad(token string) ([]Product, error) {
	cacheMutex.RLock()
	if time.Since(cacheUpdateTime) < cacheValidDuration {
		defer cacheMutex.RUnlock()
		return productCache, nil
	}
	cacheMutex.RUnlock()

	endpoint := "https://api.moysklad.ru/api/remap/1.2/entity/assortment?limit=10"
	body, err := fetchFromMoySklad(token, endpoint)
	if err != nil {
		return nil, err
	}

	var response struct {
		Rows []Product `json:"rows"`
	}
	err = json.Unmarshal(body, &response)
	if err != nil {
		return nil, err
	}

	cacheMutex.Lock()
	productCache = response.Rows
	cacheUpdateTime = time.Now()
	cacheMutex.Unlock()

	return response.Rows, nil
}

// Поиск продуктов в MoySklad
func searchProductsInMoySklad(token, searchQuery string) ([]Product, error) {
	escapedQuery := url.QueryEscape(searchQuery)
	endpoint := fmt.Sprintf("https://api.moysklad.ru/api/remap/1.2/entity/assortment?search=%s", escapedQuery)
	body, err := fetchFromMoySklad(token, endpoint)
	if err != nil {
		return nil, err
	}

	var response struct {
		Rows []Product `json:"rows"`
	}
	err = json.Unmarshal(body, &response)
	if err != nil {
		return nil, err
	}

	queryParts := strings.Fields(strings.ToLower(searchQuery))
	var filteredProducts []Product
	for _, product := range response.Rows {
		name := strings.ToLower(product.Name)
		match := true
		for _, part := range queryParts {
			if !strings.Contains(name, part) {
				match = false
				break
			}
		}
		if match {
			filteredProducts = append(filteredProducts, product)
		}
	}

	return filteredProducts, nil
}

