	userCart.Mutex.Lock()
	defer userCart.Mutex.Unlock()

	totalPrice := 0.0
	totalQuantity := 0
	for _, item := range userCart.Items {
		price := 0.0
		if len(item.Product.SalePrices) > 0 {
			price = item.Product.SalePrices[0].Value
		}
		totalPrice += float64(item.Quantity) * price / 100
