    "server_url": os.getenv("BEACON_SERVER_URL", "https://your-server.com"),
    "username": os.getenv("BEACON_USERNAME", "your_username"),
    "password": os.getenv("BEACON_PASSWORD", "your_password"),
    "beacon_interval": int(os.getenv("BEACON_INTERVAL", 60)),
    "client_id": str(uuid.uuid4()),
    "ca_bundle_path": certifi.where(),
    "encryption_key": b"your_32_character_key_here"  # Replace with a secure Fernet key
}

