function findMatches(aArray, bArray) {
    const result = [];
    const usedB = new Array(bArray.length).fill(false);

    // 一对一匹配
    function matchOneToOne() {
        for (let i = 0; i < aArray.length; i++) {
            for (let j = 0; j < bArray.length; j++) {
                if (aArray[i] === bArray[j] && !usedB[j]) {
                    usedB[j] = true;
                    result.push([[aArray[i]], [bArray[j]]]);
                    break; // 找到匹配后退出内层循环
                }
            }
        }
    }

    // 一对多匹配
    function matchOneToMany() {
        for (let i = 0; i < aArray.length; i++) {
            const target = aArray[i];
            let sum = 0;
            let startIndex = 0; // 用于记录当前组合的起始索引

            for (let j = 0; j < bArray.length; j++) {
                if (!usedB[j]) {
                    sum += bArray[j];

                    if (sum === target) {
                        for (let k = startIndex; k <= j; k++) {
                            usedB[k] = true; // 标记已使用
                        }
                        result.push([[aArray[i]], bArray.slice(startIndex, j + 1)]);
                        break; // 找到组合后退出内层循环
                    }

                    // 如果超出目标，可以提前结束
                    if (sum > target) {
                        sum = 0;
                        startIndex = j + 1;
                    }
                } else {
                    sum = 0;
                    startIndex = j + 1;
                }
            }
        }
    }

    // 多对一匹配
    function matchManyToOne() {
        for (let i = 0; i < aArray.length; i++) {
            for (let j = i + 1; j < aArray.length; j++) {
                const sum = aArray[i] + aArray[j];
                for (let k = 0; k < bArray.length; k++) {
                    if (sum === bArray[k] && !usedB[k]) {
                        usedB[k] = true; // 标记已使用
                        result.push([[aArray[i], aArray[j]], [bArray[k]]]);
                        break; // 找到匹配后退出内层循环
                    }
                }
            }
        }
    }

    // 多对多匹配
    function matchManyToMany() {
        const aCombinations = []; // 存储 A 表的所有组合

        // 生成所有 A 表的组合
        const generateCombinations = (start, currentCombination) => {
            if (currentCombination.length > 0) {
                aCombinations.push([...currentCombination]);
            }
            for (let i = start; i < aArray.length; i++) {
                currentCombination.push(aArray[i]);
                generateCombinations(i + 1, currentCombination);
                currentCombination.pop(); // 回溯
            }
        };

        generateCombinations(0, []);

        // 对于每个组合，查找匹配的 B 表元素
        aCombinations.forEach(combination => {
            const sum = combination.reduce((acc, val) => acc + val, 0);
            for (let k = 0; k < bArray.length; k++) {
                if (sum === bArray[k] && !usedB[k]) {
                    usedB[k] = true; // 标记已使用
                    result.push([combination, [bArray[k]]]);
                    break; // 找到匹配后退出内层循环
                }
            }
        });
    }

    // 执行所有匹配
    matchOneToOne();
    matchOneToMany();
    matchManyToOne();
    matchManyToMany();

    // 处理剩余的元素
    const remainingA = aArray.filter((_, i) => !result.some(([a, _]) => a.includes(aArray[i])));
    const remainingB = bArray.filter((_, i) => !usedB[i]);

    // 如果还有剩余的 A 和 B，可以将其合并为一组
    if (remainingA.length > 0 || remainingB.length > 0) {
        result.push([remainingA, remainingB]);
    }

    return result;
}

