using UnityEngine;
using UnityEngine.UI;
using TMPro; // Import TextMeshPro namespace
using System.Collections;
using System.Collections.Generic;

public class SpaceStation : MonoBehaviour
{
    [System.Serializable]
    public class Upgrade
    {
        public string name;
        public int level;
        public float baseCost;
        public float costMultiplier;
        public string resourceType;
        public float buildTime; // Time required to build the upgrade in seconds
        public float buildProgress; // Current progress towards completing the build (in seconds)
        public bool isBuilding; // Flag to indicate if the upgrade is currently being built

        // Constructor to initialize fields
        public Upgrade(string _name, int _level, float _baseCost, float _costMultiplier, string _resourceType, float _buildTime)
        {
            name = _name;
            level = _level;
            baseCost = _baseCost;
            costMultiplier = _costMultiplier;
            resourceType = _resourceType;
            buildTime = _buildTime;
            buildProgress = 0f;
            isBuilding = false;
        }
    }

    public ResourceManager resourceManager;
    public List<Upgrade> upgrades = new List<Upgrade>();
    public GameObject upgradePanelPrefab;
    public Transform upgradeContainer;
    public Canvas upgradeCanvas; // Assign this in the inspector


    private Camera mainCamera;
    private Dictionary<string, GameObject> upgradePanels = new Dictionary<string, GameObject>();
    private bool isUIVisible = false;

    private void Start()
    {
        Debug.Log("SpaceStation Start method called");
        InitializeUpgrades();
        mainCamera = Camera.main;
        upgradeCanvas.worldCamera = mainCamera;
        HideUpgradeUI(); // Hide UI at start
    }

    private void InitializeUpgrades()
    {
        Debug.Log($"Initializing {upgrades.Count} upgrades");
        foreach (var upgrade in upgrades)
        {
            CreateUpgradePanel(upgrade);
        }
    }

    private void CreateUpgradePanel(Upgrade upgrade)
    {
        Debug.Log($"Creating panel for upgrade: {upgrade.name}");
        GameObject panel = Instantiate(upgradePanelPrefab, upgradeCanvas.transform);
        panel.name = upgrade.name + "Panel";

        // Finding child components by name using TextMeshPro components
        TMP_Text nameText = panel.transform.Find("NameText").GetComponent<TMP_Text>();
        TMP_Text levelText = panel.transform.Find("LevelText").GetComponent<TMP_Text>();
        TMP_Text costText = panel.transform.Find("CostText").GetComponent<TMP_Text>();
        TMP_Text buildProgressText = panel.transform.Find("BuildProgressText").GetComponent<TMP_Text>(); // Text to display build progress
        Button upgradeButton = panel.GetComponentInChildren<Button>();

        if (nameText == null || levelText == null || costText == null || buildProgressText == null || upgradeButton == null)
        {
            Debug.LogError("One or more UI components not found in the prefab");
            return;
        }

        nameText.text = upgrade.name;
        UpdateUpgradePanel(upgrade, panel);

        upgradeButton.onClick.AddListener(() => StartBuilding(upgrade));

        upgradePanels[upgrade.name] = panel;
    }

    private void UpdateUpgradePanel(Upgrade upgrade, GameObject panel)
    {
        TMP_Text levelText = panel.transform.Find("LevelText").GetComponent<TMP_Text>();
        TMP_Text costText = panel.transform.Find("CostText").GetComponent<TMP_Text>();
        TMP_Text buildProgressText = panel.transform.Find("BuildProgressText").GetComponent<TMP_Text>();

        if (levelText == null || costText == null || buildProgressText == null)
        {
            Debug.LogError("One or more TextMeshPro components not found in the panel");
            return;
        }

        levelText.text = "Level: " + upgrade.level;
        costText.text = "Cost: " + GetUpgradeCost(upgrade).ToString("F2") + " " + upgrade.resourceType;

        if (upgrade.isBuilding)
        {
            buildProgressText.text = "Building... " + (upgrade.buildTime - upgrade.buildProgress).ToString("F1") + "s";
        }
        else
        {
            buildProgressText.text = "Ready to upgrade";
        }
    }

    public void StartBuilding(Upgrade upgrade)
    {
        if (!upgrade.isBuilding)
        {
            float cost = GetUpgradeCost(upgrade);
            if (resourceManager.resources.ContainsKey(upgrade.resourceType) &&
                resourceManager.resources[upgrade.resourceType] >= cost)
            {
                resourceManager.AddOrUpdateResource(upgrade.resourceType, -cost);
                upgrade.isBuilding = true;
                StartCoroutine(BuildUpgrade(upgrade));
            }
            else
            {
                Debug.Log("Not enough resources to start building " + upgrade.name);
            }
        }
    }

    private IEnumerator BuildUpgrade(Upgrade upgrade)
    {
        while (upgrade.buildProgress < upgrade.buildTime)
        {
            yield return new WaitForSeconds(1f); // Wait for 1 second
            upgrade.buildProgress += 1f; // Increase progress by 1 second
            UpdateUpgradePanel(upgrade, upgradePanels[upgrade.name]);
        }

        upgrade.level++; // Upgrade completed, increase level
        upgrade.isBuilding = false;
        upgrade.buildProgress = 0f;
        UpdateUpgradePanel(upgrade, upgradePanels[upgrade.name]);
    }

    private float GetUpgradeCost(Upgrade upgrade)
    {
        return upgrade.baseCost * Mathf.Pow(upgrade.costMultiplier, upgrade.level);
    }

    private void Update()
    {
        // Check for touch input
        if (Input.touchCount > 0 && Input.GetTouch(0).phase == TouchPhase.Began)
        {
            Ray ray = mainCamera.ScreenPointToRay(Input.GetTouch(0).position);
            RaycastHit hit;

            if (Physics.Raycast(ray, out hit) && hit.collider.gameObject == gameObject)
            {
                ToggleUpgradeUI();
            }
            else
            {
                HideUpgradeUI();
            }
        }

        // Update UI position to follow the space station
        if (upgradeCanvas.gameObject.activeSelf)
        {
            upgradeCanvas.transform.position = mainCamera.WorldToScreenPoint(transform.position);
        }
        if (upgradeCanvas.gameObject.activeSelf)
        {
            upgradeCanvas.transform.LookAt(mainCamera.transform);
            upgradeCanvas.transform.Rotate(0, 180, 0); // Make it face the camera correctly
        }

    }

