        setupValidation() {
            this.setupFieldValidation(this.nameInput, this.validateName);
            this.setupFieldValidation(this.streetInput, this.validateStreet);
            this.form.on('submit', this.onFormSubmit.bind(this));
        }

        setupFieldValidation(input, validationFunction) {
            if (!input.length) return;

            input.on('input', _.debounce(() => {
                this.validateAndShowFeedback(input, validationFunction);
            }, 300));

            input.on('blur', () => {
                this.validateAndShowFeedback(input, validationFunction);
            });
        }

        validateAndShowFeedback(input, validationFunction) {
            const result = validationFunction(input.val().trim());
            const feedback = this.getOrCreateFeedbackElement(input);

            input.toggleClass('is-invalid', !result.isValid);
            input.toggleClass('is-valid', result.isValid && input.val().trim() !== '');

            if (!result.isValid) {
                feedback.text(result.message).removeClass('valid-feedback').addClass('invalid-feedback').show();
            } else if (input.val().trim() !== '') {
                feedback.text(_t('Looks good!')).removeClass('invalid-feedback').addClass('valid-feedback').show();
            } else {
                feedback.hide();
            }
        }

        getOrCreateFeedbackElement(input) {
            const feedbackId = `${input.attr('name')}-feedback`;
            let feedback = $(`#${feedbackId}`);
            if (!feedback.length) {
                feedback = $('<div>', {
                    id: feedbackId,
                    class: 'feedback',
                    'aria-live': 'polite'
                }).insertAfter(input);
            }
            return feedback;
        }

        validateName(name) {
            const nameRegex = /^[A-ZČĆĐŠŽ][a-zčćđšž]+(-[A-ZČĆĐŠŽ][a-zčćđšž]+)?(\s+[A-ZČĆĐŠŽ][a-zčćđšž]+(-[A-ZČĆĐŠŽ][a-zčćđšž]+)?)+$/;

            if (name.length === 0) {
                return { isValid: false, message: _t("Name is required.") };
            }
            if (name.includes('  ')) {
                return { isValid: false, message: _t("Name contains double spaces. Please remove them.") };
            }
            if (!nameRegex.test(name)) {
                return { isValid: false, message: _t("Please enter a valid full name (at least two words, starting with capital letters). For hyphenated names, ensure correct capitalization.") };
            }
            return { isValid: true, message: "" };
        }

        validateStreet(street) {
            const streetRegex = /^[a-zA-ZčČćĆđĐšŠžŽ\s\d.,'-]+(,?\s*(br\.\s*)?)?\d+[a-zA-Z]?(\/?\d+[a-zA-Z]?)?(-?\d+[a-zA-Z]?)?$/i;

            console.log('Validating street:', street);
            console.log('Regex test result:', streetRegex.test(street));

            if (street.trim().length === 0) {
                return { isValid: false, message: _t("Street address is required.") };
            }
            if (!streetRegex.test(street)) {
                if (!/^[a-zA-ZčČćĆđĐšŠžŽ\s\d.,'-]+/.test(street)) {
                    return { isValid: false, message: _t("Invalid characters in street name.") };
                }
                if (/br\./i.test(street) && !/br\.\s*\d/i.test(street)) {
                    return { isValid: false, message: _t("Incorrectly formatted 'br.'.") };
                }
                if (!/\d/.test(street)) {
                    return { isValid: false, message: _t("Missing house number.") };
                }
                if (/[!@#$%^&*()_+={}[\]|;:"<>?~`]/.test(street)) {
                    return { isValid: false, message: _t("Invalid special characters in address.") };
                }
                if (/\d{2,}\/\d{2,}/.test(street)) {
                    return { isValid: false, message: _t("Too many digits around the slash in house number.") };
                }
                if (/\d{2,}-\d{2,}/.test(street)) {
                    return { isValid: false, message: _t("Too many digits around the hyphen in house number.") };
                }
                if (/\/\//.test(street)) {
                    return { isValid: false, message: _t("Double slashes in house number.") };
                }
                if (/--/.test(street)) {
                    return { isValid: false, message: _t("Double hyphens in house number.") };
                }
                return { isValid: false, message: _t("Please enter a valid Croatian street address (e.g., Ilica 5, Vukovarska ulica 72A, or Ulica 64, br. 5a).") };
            }
            return { isValid: true, message: "" };
        }

        onFormSubmit(event) {
            const isNameValid = this.validateName(this.nameInput.val().trim()).isValid;
            const isStreetValid = this.validateStreet(this.streetInput.val().trim()).isValid;

            this.validateAndShowFeedback(this.nameInput, this.validateName);
            this.validateAndShowFeedback(this.streetInput, this.validateStreet);

            if (!isNameValid || !isStreetValid) {
                event.preventDefault();
                event.stopPropagation();
            }
        }
    }

    publicWidget.registry.AddressValidation = publicWidget.Widget.extend({
        selector: 'form.checkout_autoformat',
        start: function () {
            new AddressValidation(this.el);
        },
    });

    return AddressValidation;
});
