// app/components/apartment/ApartmentList.jsx
"use client";
import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { fetchApartments } from '../../api/apartments/getApartments';

const ApartmentList = () => {
    const [apartments, setApartments] = useState([]);

    useEffect(() => {
        const getApartments = async () => {
            try {
                const result = await fetchApartments();
                setApartments(result.data);
            } catch (error) {
                console.error('Failed to load apartments', error);
            }
        };
        getApartments();
    }, []);

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
            {apartments.length > 0 ? (
                apartments.map((apartment) => (
                    <Link href={`/apartments/${apartment.id}`} key={apartment.id}>
                        <div className="border rounded-lg shadow-lg p-4 cursor-pointer hover:shadow-xl transition-shadow">
                            <h2 className="text-xl font-bold">{apartment.title}</h2>
                            <p className="text-gray-600">{apartment.description.substring(0, 100)}...</p>
                            <p className="text-lg font-semibold">Giá: ${apartment.price}</p>
                            <p className="text-sm text-gray-500">Địa chỉ: {apartment.address}</p>
                            {apartment.images.length > 0 && (
                                <img
                                    src={apartment.images[0].url}
                                    alt={apartment.title}
                                    className="mt-2 h-48 w-full object-cover rounded"
                                />
                            )}
                        </div>
                    </Link>
                ))
            ) : (
                <p>No apartments found.</p>
            )}
        </div>
    );
};

export default ApartmentList;

