This is vector.hpp, remember it and type "OK":

```
#pragma once

#include "../memory/memory.hpp"
#include "../utils/algorithm.hpp"
#include "../utils/utility.hpp"
#include "../../MemoryManager.h"
#include <stdint.h>

namespace nostd {
	template <typename T>
	class vector {
	private:
		T* m_data;
		size_t m_size;
		size_t m_capacity;

		__forceinline void reallocData(size_t newCapacity) {
			m_capacity = newCapacity;
			T* newData = (T*)MMAlloc(sizeof(T) * m_capacity);
			for (size_t i = 0; i < m_size; ++i) {
				new (&newData[i]) T(nostd::move(m_data[i]));
				m_data[i].~T();
			}
			if (m_data) {
				MMFree(m_data);
			}
			m_data = newData;
		}

	public:
		using value_type = T;
		using iterator = T*;
		using const_iterator = const T*;

		__forceinline vector() : m_data(nullptr), m_size(0), m_capacity(0) {}

		__forceinline vector(size_t size) : m_data(nullptr), m_size(size), m_capacity(size) {
			m_data = (T*)MMAlloc(sizeof(T) * m_capacity);
			for (size_t i = 0; i < m_size; ++i) {
				new (&m_data[i]) T();
			}
		}

		__forceinline vector(const std::initializer_list<T>& initList) : m_data(nullptr), m_size(initList.size()), m_capacity(initList.size()) {
			m_data = (T*)MMAlloc(sizeof(T) * m_capacity);
			size_t i = 0;
			for (const T& elem : initList) {
				new (&m_data[i++]) T(elem);
			}
		}

		__forceinline vector(const vector& other) : m_data(nullptr), m_size(other.m_size), m_capacity(other.m_capacity) {
			m_data = (T*)MMAlloc(sizeof(T) * m_capacity);
			for (size_t i = 0; i < m_size; ++i) {
				new (&m_data[i]) T(other.m_data[i]);
			}
		}

		__forceinline vector(vector&& other) : m_data(other.m_data), m_size(other.m_size), m_capacity(other.m_capacity) {
			other.m_data = nullptr;
			other.m_size = 0;
			other.m_capacity = 0;
		}

		__forceinline vector& operator=(const vector& other) {
			if (this != &other) {
				clear();
				MMFree(m_data);

				m_size = other.m_size;
				m_capacity = other.m_capacity;
				m_data = (T*)MMAlloc(sizeof(T) * m_capacity);
				for (size_t i = 0; i < m_size; ++i) {
					new (&m_data[i]) T(other.m_data[i]);
				}
			}
			return *this;
		}

		__forceinline vector& operator=(vector&& other) {
			if (this != &other) {
				clear();
				MMFree(m_data);

				m_data = other.m_data;
				m_size = other.m_size;
				m_capacity = other.m_capacity;

				other.m_data = nullptr;
				other.m_size = 0;
				other.m_capacity = 0;
			}
			return *this;
		}

		__forceinline ~vector() {
			clear();
			if (m_data) {
				MMFree(m_data);
			}
		}

		__forceinline void push_back(const T& value) {
			if (m_size == m_capacity) {
				reallocData((m_capacity == 0) ? 1 : m_capacity * 2);
			}
			new (&m_data[m_size++]) T(value);
		}

		__forceinline void push_back(T&& value) {
			if (m_size == m_capacity) {
				reallocData((m_capacity == 0) ? 1 : m_capacity * 2);
			}
			new (&m_data[m_size++]) T(nostd::move(value));
		}

		__forceinline void pop_back() {
			if (m_size > 0) {
				m_data[--m_size].~T();
			}
		}

		__forceinline size_t size() const {
			return m_size;
		}

		__forceinline size_t capacity() const {
			return m_capacity;
		}

		__forceinline bool empty() const {
			return m_size == 0;
		}

		__forceinline void clear() {
			for (size_t i = 0; i < m_size; ++i) {
				m_data[i].~T();
			}
			m_size = 0;
		}

		__forceinline void reserve(size_t newCapacity) {
			if (newCapacity > m_capacity) {
				reallocData(newCapacity);
			}
		}

		__forceinline void resize(size_t newSize) {
			if (newSize > m_capacity) {
				reallocData(newSize);
			}
			if (newSize > m_size) {
				for (size_t i = m_size; i < newSize; ++i) {
					new (&m_data[i]) T();
				}
			} else {
				for (size_t i = newSize; i < m_size; ++i) {
					m_data[i].~T();
				}
			}
			m_size = newSize;
		}

		__forceinline T& at(size_t index) {
			return m_data[index];
		}

		__forceinline const T& at(size_t index) const {
			return m_data[index];
		}

		__forceinline T& operator[](size_t index) {
			return m_data[index];
		}

		__forceinline const T& operator[](size_t index) const {
			return m_data[index];
		}

		__forceinline T* data() {
			return m_data;
		}

		__forceinline const T* data() const {
			return m_data;
		}

		__forceinline iterator begin() {
			return m_data;
		}

		__forceinline const_iterator begin() const {
			return m_data;
		}

		__forceinline iterator end() {
			return m_data + m_size;
		}

		__forceinline const_iterator end() const {
			return m_data + m_size;
		}
	};
}

