    } else if ranks.values().any(|&count| count == 3) {
        let mut rank_values = get_rank_values(&ranks, 3);
        rank_values.extend(get_rank_values(&ranks, 1));
        (3, // Three of a kind
         rank_values)
    } else if ranks.values().filter(|&&count| count == 2).count() == 2 {
        let mut rank_values = get_rank_values(&ranks, 2);
