            union_sets(uf, A-1, B-1);
        }

        int max_size = 0;

        for (int i = 0; i < N; i++) {
            int root = find(uf, i);
            if (uf->size[root] > max_size) {
                max_size = uf->size[root];
            }
        }

        printf("%d\n", max_size);

        free(uf->parent);
        free(uf->rank);
        free(uf->size);
        free(uf);
    }

    return 0;
}

