int count_reachable_cells(int n, vector<int>& d)
{
    vector<std::vector<int>> dp(n, vector<int>(n, 0));
    dp[0][0] = 1;

    for (int x = 0; x < n; x++) {
        for (int y = 0; y < n; y++) {
            for (int i = 0; i < d.size(); i++) {
                if (x >= d[i]) {
                    dp[x][y] += dp[x-d[i]][y];
                }
                if (y >= d[i]) {
                    dp[x][y] += dp[x][y-d[i]];
                }
            }
        }
    }

    int total = 0;
    for (auto& row : dp) {
        for (int cell : row) {
            total += cell;
        }
    }
    return total;
}

int main() {
    int n = 2;
    vector<int> d = {4, 2};
    cout << count_reachable_cells(n, d) << endl;
    return 0;
}
