import 'dart:io';

class TrieNode {
  late Map<String, int> countMap;
  late Map<String, TrieNode> children;

  TrieNode() {
    countMap = {};
    children = {};
  }
}

class Trie {
  late TrieNode root;

  Trie() {
    root = TrieNode();
  }

  void insert(String word) {
    TrieNode node = root;
    for (int i = 0; i < word.length; i++) {
      String char = word[i];
      if (!node.children.containsKey(char)) {
        node.children[char] = TrieNode();
      }
      node = node.children[char]!;
      node.countMap[word] = (node.countMap[word] ?? 0) + 1;
    }
  }

  String findMostFrequentWithPrefix(String prefix) {
    TrieNode node = root;
    for (int i = 0; i < prefix.length; i++) {
      String char = prefix[i];
      if (!node.children.containsKey(char)) {
        return prefix; 
      }
      node = node.children[char]!;
    }

    String mostFrequent = prefix;
    int maxCount = 0;

    for (var entry in node.countMap.entries) {
      if (entry.value > maxCount ||
          (entry.value == maxCount && entry.key.compareTo(mostFrequent) < 0)) {
        mostFrequent = entry.key;
        maxCount = entry.value;
      }
    }

    return mostFrequent;
  }
}

void main() {
  Trie trie = Trie();


  int n = int.parse(stdin.readLineSync()!);
  List<String> queries = [];

  for (int i = 0; i < n; i++) {
    String query = stdin.readLineSync()!;
    queries.add(query);
    List<String> parts = queries[i].split(" ");
    if (parts[0] == "+") {
      int count = int.parse(parts[1]);
      String word = parts[2];
      for (int j = 0; j < count; j++) {
        trie.insert(word);
      }
    } else if (parts[0] == "?") {
      String prefix = parts[1];
      String result = trie.findMostFrequentWithPrefix(prefix);
      print(result);
    }
  }
}
