package main

import (
        "flag"
        "fmt"
        "io/ioutil"
        "os"
        "path/filepath"
        "strings"
)

func main() {
        dirPath := flag.String("d", "", "Directory path to traverse")
        fileName := flag.String("f", "", "File name to search")
        oldString := flag.String("o", "", "Old string to search for")
        newString := flag.String("n", "", "New string to replace with")

        flag.Parse()

        if *dirPath == "" || *fileName == "" || *oldString == "" || *newString == "" {
                fmt.Println("Please provide all required parameters: -d, -f, -o, -n")
                flag.Usage()
                return
        }

        err := filepath.Walk(*dirPath, func(path string, info os.FileInfo, err error) error {
                if err != nil {
                        return err
                }

                if !info.IsDir() && info.Name() == *fileName {
                        // fmt.Println("Processing:", path)
                        read, err := ioutil.ReadFile(path)
                        if err != nil {
                                return err
                        }

                        content := string(read)
                        updatedContent := strings.ReplaceAll(content, *oldString, *newString)

                        err = ioutil.WriteFile(path, []byte(updatedContent), 0644)
                        if err != nil {
                                return err
                        }
                        fmt.Println("Updated:", path)
                }

                return nil
        })

        if err != nil {
                fmt.Println("Error:", err)
        }
}
