perform_t_test <- function(data, var, group, mu) {
  if (group == "None") {
    result <- t.test(data[[var]], mu = mu)
  } else {
    result <- t.test(data[[var]] ~ data[[group]])
  }
  capture.output(print(result)) %>% paste(collapse = "\n")
}

