public static int[] FindBestCheckpointsOrder(Point[] checkpoints)
{
    var bestOrder = MakeTrivialPermutation(checkpoints.Length);
    var minLength = checkpoints.GetPathLength(bestOrder);

    FindBestOrderRecursive(checkpoints, new int[checkpoints.Length], 0, ref bestOrder, ref minLength);

    return bestOrder;
}

private static void FindBestOrderRecursive(Point[] checkpoints, int[] currentOrder, int currentIndex, ref int[] bestOrder, ref double minLength)
{
    if (currentIndex == checkpoints.Length)
    {
        var currentLength = checkpoints.GetPathLength(currentOrder);
        if (currentLength < minLength)
        {
            minLength = currentLength;
            bestOrder = (int[])currentOrder.Clone();
        }
        return;
    }

    for (var i = 0; i < checkpoints.Length; i++)
    {
        if (!currentOrder.Contains(i))
        {
            currentOrder[currentIndex] = i;
            if (checkpoints.GetPathLength(currentOrder.Take(currentIndex + 1).ToArray()) >= minLength)
            {
                continue;
            }
            FindBestOrderRecursive(checkpoints, currentOrder, currentIndex + 1, ref bestOrder, ref minLength);
        }
    }
}

private static int[] MakeTrivialPermutation(int size)
{
    var bestOrder = new int[size];
    for (var i = 0; i < bestOrder.Length; i++)
    {
        bestOrder[i] = i;
    }            
    return bestOrder;
}

