    String data = emailController.value.text;
    if (data.isEmpty) {
      return "Email is required";
    } else if (hasProhibitedCharacters(data)) {
      return "Email contains prohibited characters";
    } else if (!isLengthValid(data)) {
      return "Email length is not valid";
    }
    return null;
  }

