import 'package:sail/core/bases/BaseRepository.dart';

abstract class MResponse<T> {
  void fold(
    Function(String errorMessage, [StatusCode? code, T? data]) onError,
    Function(T data, [String? message]) onSuccess,
  );
}

class Failure<T> extends MResponse<T> {
  final String errorMessage;
  final T? data;
  final StatusCode? code;

  Failure(this.errorMessage, [this.code, this.data]);

  @override
  void fold(Function(String errorMessage, [StatusCode? code, T? data]) onError,
      Function(T data, [String? message]) onSuccess) {
    onError(errorMessage, code, data);
  }
}

class ValidationFailure<T> extends MResponse<T> {
  final Map<String, dynamic> errorMessages;

  ValidationFailure(this.errorMessages);

  @override
  void fold(Function(String errorMessage, [StatusCode? code, T? data]) onError,
      Function(T data, [String? message]) onSuccess) {
    onError('Validation failed', null, null);
  }
}

class Success<T> extends MResponse<T> {
  final T data;
  String? message;

  Success(T this.data, [this.message]);

  @override
  void fold(Function(String errorMessage, [StatusCode? code, T? data]) onError,
      Function(T data, [String? message]) onSuccess) {
    onSuccess(data, message);
  }
}

