      return "Email contains prohibited characters";
    } else if (!isLengthValid(data)) {
      return "Email length is not valid";
    }
    return null;
  }

  Future<String?> validatePhoneNumber() async {
    String data = phoneNumberController.value.text;
    if (data.isEmpty) {
      return "Phone number is required";
    }
    data = data.replaceAll(RegExp(r'\s+|\D+'), '');
    if (data.length != 11) {
      return "Phone number must be 11 digits";
    }
    return null;
  }

  Future<String?> validateUserName() async {
    String data = userNameController.value.text;
    if (data.isEmpty) {
      return 'Name cannot be empty';
    } else if (data.length < 3) {
      return 'Name must be at least 3 characters long';
    } else {
      return null;
    }
  }

  bool hasProhibitedCharacters(String email) {
    final localPart = email.split('@').first;
    final prohibitedCharacters = RegExp(r'[()<>@,;:\"\\[\] ]');
    return prohibitedCharacters.hasMatch(localPart);
  }

  bool isLengthValid(String email) {
    final localPart = email.split('@').first;
    final domainPart = email.split('@').last;
    return localPart.length <= 64 && domainPart.length <= 255;
  }

