    connect(mDevice, &DeviceAbstract::connectedToDevice, mDevice, &DeviceAbstract::request_settings, Qt::QueuedConnection);

    cTimer->setInterval(time_reconnection);
    cTimer->setTimerType(Qt::TimerType::PreciseTimer);
    connect(cTimer, &QTimer::timeout,  mDevice, &DeviceAbstract::connectToDevice, Qt::ConnectionType::QueuedConnection);

    //thrad setup
    mDevice->moveToThread(thread);

    connect(mDevice, &DeviceAbstract::connectionError, this, &NR10_widget::setStatusError, Qt::QueuedConnection);
    connect(mDevice, &DeviceAbstract::connectedToDevice, this, &NR10_widget::connected, Qt::QueuedConnection);
    connect(mDevice, &DeviceAbstract::settings_updated, this, &NR10_widget::param_update, Qt::QueuedConnection);
    connect(mDevice, &DeviceAbstract::data_updated, this, &NR10_widget::data_update, Qt::QueuedConnection);
    connect(mDevice, &DeviceAbstract::connectedDiffDevice, this, &NR10_widget::connectedDiffDevice);
    connect(mDevice, &DeviceAbstract::disconnectedFromDevice, this, &NR10_widget::disconnected);
    connect(mDevice, &DeviceAbstract::device_updated, [this](){status->setText("Настройки изменены!");});

    connect(accept, &QPushButton::clicked, this, &NR10_widget::set_settings);
    connect(cancel, &QPushButton::clicked, this, &NR10_widget::load_settings);
    connect(connect_button, &QPushButton::clicked, this, &NR10_widget::on_connect_clicked);


    connect(this, &NR10_widget::setup_device, mDevice, &DeviceAbstract::setup_device, Qt::QueuedConnection);
    connect(this, &NR10_widget::connectDevice, mDevice, &DeviceAbstract::connectToDevice, Qt::QueuedConnection);
    connect(this, &NR10_widget::disconnectDevice, mDevice, &DeviceAbstract::disconnect, Qt::QueuedConnection);

    thread->start();
    cTimer->start();
}

NR10_widget::~NR10_widget()
{
    if(mTimer->isActive()) mTimer->stop();
    delete mTimer;
    mTimer = nullptr;

    if(pTimer->isActive()) pTimer->stop();
    delete pTimer;
    pTimer = nullptr;

    if(cTimer->isActive()) cTimer->stop();
    delete cTimer;
    cTimer = nullptr;

    emit disconnectDevice();
    thread->quit();
    thread->deleteLater();
}

可以把重载的函数里面关于绘制矩形的内容写成信号与槽吗，重载的函数负责发送信号，槽函数在QWidget中实现
// customgraphicsview.h
#ifndef CUSTOMGRAPHICSVIEW_H
#define CUSTOMGRAPHICSVIEW_H

#include <QGraphicsView>
#include <QGraphicsScene>
#include <QGraphicsRectItem>

class CustomGraphicsView : public QGraphicsView
{
    Q_OBJECT

public:
    CustomGraphicsView(QWidget *parent = nullptr);

protected:
    void mousePressEvent(QMouseEvent *event) override;
    void mouseMoveEvent(QMouseEvent *event) override;
    void mouseReleaseEvent(QMouseEvent *event) override;

private:
    QGraphicsScene *scene;
    QPointF startPoint;
    QGraphicsRectItem *currentRect;
};

#endif // CUSTOMGRAPHICSVIEW_H

// customgraphicsview.cpp
#include "customgraphicsview.h"
#include <QMouseEvent>

CustomGraphicsView::CustomGraphicsView(QWidget *parent)
    : QGraphicsView(parent), currentRect(nullptr)
{
    scene = new QGraphicsScene(this);
    setScene(scene);
}

void CustomGraphicsView::mousePressEvent(QMouseEvent *event)
{
    if (event->button() == Qt::LeftButton) {
        startPoint = mapToScene(event->pos());
        currentRect = new QGraphicsRectItem();
        currentRect->setBrush(QBrush(QColor(0, 0, 255, 100)));
        scene->addItem(currentRect);
    }
    QGraphicsView::mousePressEvent(event);
}

void CustomGraphicsView::mouseMoveEvent(QMouseEvent *event)
{
    if (currentRect) {
        QPointF endPoint = mapToScene(event->pos());
        QRectF rect = QRectF(startPoint, endPoint).normalized();
        currentRect->setRect(rect);
    }
    QGraphicsView::mouseMoveEvent(event);
}

void CustomGraphicsView::mouseReleaseEvent(QMouseEvent *event)
{
    if (event->button() == Qt::LeftButton && currentRect) {
        currentRect = nullptr;
    }
    QGraphicsView::mouseReleaseEvent(event);
}

