point_at_ratio(f){
	// Calculate the difference in angle, taking into account the direction (ccw or cw)
	let dtheta = (this.is_ccw) ? this.theta2 - this.theta1 : this.theta1 - this.theta2;
	// Ensure dtheta is within -pi to +pi
	dtheta = rads(dtheta); 
	// Calculate the angle at the desired ratio
	let theta = (this.is_ccw) ? this.theta1 + f * dtheta : this.theta1 - f * dtheta;
	// Get the point on the circle at the calculated angle
	return this.circle.point(theta);
}

// point on circumference at ratio f from p1 to p2
point_at_ratio(f) {
	let theta1 = rads(this.theta1);
	let theta2 = rads(this.theta2);

	let deltaTheta;
	if (this.is_ccw) {
		if (theta2 >= theta1) {
			deltaTheta = theta2 - theta1;
		} else {
			deltaTheta = 2 * Math.PI - (theta1 - theta2);
		}
	} else {
		if (theta1 >= theta2) {
			deltaTheta = theta1 - theta2;
		} else {
			deltaTheta = 2 * Math.PI - (theta2 - theta1);
		}
	}

	let targetTheta = theta1 + (this.is_ccw ? 1 : -1) * deltaTheta * f;
	targetTheta = rads(targetTheta);

	return this.circle.point(targetTheta);
}

// point on circumference at ratio f from p1 to p2
point_at_ratio(f) {
	let delta_theta = rads(this.theta2 - this.theta1);
	if (!this.is_ccw) {
		if (delta_theta > 0) {
			delta_theta -= 2 * Math.PI; // adjust for CW
		}
	} else {
		if (delta_theta < 0) {
			delta_theta += 2 * Math.PI; // adjust for CCW
		}
	}
	let theta = rads(this.theta1 + f * delta_theta);
	return this.circle.point(theta);
}

// point on circumference at ratio f from p1 to p2
point_at_ratio(f) {
	// Normalize the ratio to be between 0 and 1
	f = Math.max(0, Math.min(1, f));

	// Convert the angles to be within the acceptable range of -pi to pi
	let theta1 = rads(this.theta1);
	let theta2 = rads(this.theta2);

	// Calculate the difference between the angles, taking direction into account
	let dtheta = theta2 - theta1;
	if (this.is_ccw && dtheta < 0) {
		dtheta += 2 * Math.PI; 
	} else if (!this.is_ccw && dtheta > 0) {
		dtheta -= 2 * Math.PI;
	}

	// Calculate the angle at the given ratio
	let theta = theta1 + f * dtheta;

	// Return the point on the circle's circumference at the calculated angle
	return this.circle.point(theta);
