float valueNoise21(vec2 x) {
    vec2 id = floor(x);
    vec2 guv = fract(x);


	float a = hash21(floor(id + vec2(0.0, 0.0)));
    float b = hash21(floor(id + vec2(1.0, 0.0)));
    float c = hash21(floor(id + vec2(0.0, 1.0)));
    float d = hash21(floor(id + vec2(1.0, 1.0)));

    vec2 t = guv * guv * (3.0 - 2.0 * guv);


    return mix(mix(a,b,t.x),mix(c,d,t.x),t.y);
}

