    vec3 _black = vec3 (0.,0.,0.);
    vec3 _white = vec3(1.0, 1.0, 1.0);
    float _amount = 1.; // 0..1

    vec3 gray = vec3(0.299, 0.587, 0.114);  ///Grayscale coefficient  
    float brightness = dot(col, gray); //Get the brightness value
    vec3 grayColor = vec3(brightness); // Turn into black and white map

    grayColor = mix(_black, _white, grayColor);
    vec3 color = mix(col,grayColor,_amount);
    return color;
