// MARK: - Audio Recorder Model (using Facade pattern for simplified access)

class AudioRecorder: ObservableObject {
    private var audioRecorder: AVAudioRecorder?
    private var timer: Timer?

    @Published var isRecording: Bool = false
    @Published var audioLevels: [Float] = Array(repeating: 0.0, count: 50) // For equalizer effect

    // Error handling
    enum AudioRecorderError: Error {
        case initializationFailed
        case recordingFailed
        case permissionsDenied
    }

    func requestPermissions(completion: @escaping (Result<Bool, AudioRecorderError>) -> Void) {
        AVAudioSession.sharedInstance().requestRecordPermission { granted in
            DispatchQueue.main.async { // Ensure UI updates happen on the main thread
                completion(granted ? .success(true) : .failure(.permissionsDenied))
            }
        }
    }

    func startRecording() throws {
        // 1. Request Microphone Access
        let audioSession = AVAudioSession.sharedInstance()
        try audioSession.setCategory(.playAndRecord, mode: .default)
        try audioSession.setActive(true)

        // 2. Set up the file path for recording
        let documentsURL = FileManager.default.urls(for: .documentDirectory, in: .userDomainMask)[0]
        let fileName = "recording.m4a" // Adjust file type as needed
        let fileURL = documentsURL.appendingPathComponent(fileName)

        // 3. Define the recording settings
        let settings: [String: Any] = [
            AVFormatIDKey: Int(kAudioFormatMPEG4AAC),
            AVSampleRateKey: 16000.0,
            AVNumberOfChannelsKey: 1,
            AVEncoderAudioQualityKey: AVAudioQuality.high.rawValue
        ]

        // 4. Initialize and prepare the recorder
        do {
            audioRecorder = try AVAudioRecorder(url: fileURL, settings: settings)
            audioRecorder?.isMeteringEnabled = true // Enable level metering for the equalizer
            audioRecorder?.prepareToRecord()
            audioRecorder?.record()
            isRecording = true

            // Start timer to update audio levels
            timer = Timer.scheduledTimer(withTimeInterval: 0.05, repeats: true) { timer in
                self.updateAudioLevels()
            }
        } catch {
            // Handle recording initialization errors here
            isRecording = false
            throw AudioRecorderError.initializationFailed // Propagate error
        }
    }

    func stopRecording() {
        audioRecorder?.stop()
        audioRecorder = nil
        isRecording = false
        timer?.invalidate()
        timer = nil
    }

    private func updateAudioLevels() {
        guard let recorder = audioRecorder, recorder.isRecording else { return }

        recorder.updateMeters()

        var levels: [Float] = []
        let channels = recorder.format.channelCount

        let avgPower = recorder.averagePower(forChannel: 0)
        let linearLevel = pow(10.0, avgPower / 20.0) // Convert to linear scale
        levels.append(linearLevel)


        DispatchQueue.main.async {
            self.audioLevels = levels
        }
    }
}

