    ?products_count_min=5
    """

    title = CharFilter(lookup_expr='icontains')
    products_count = NumberFilter(method='filter_products_count')
    products_count_range = RangeFilter(method='filter_products_count_range')

    class Meta:
        model = Category
        fields = {
            'id': ['exact'],
        }

    def filter_products_count(self, queryset, name, value):
        return queryset.filter(products_count=value)

    def filter_products_count_range(self, queryset, name, value):
        if value.start is not None:
            return queryset.filter(products_count__gte=value.start)
        if value.stop is not None:
            return queryset.filter(products_count__lte=value.stop)

