    if (digitalRead(encoderPinA) == digitalRead(encoderPinB)) {
      encoderCount++; // Increment count if direction is clockwise
      encoderDirection = true; // Set direction to clockwise
    } else {
      encoderCount--; // Decrement count if direction is counterclockwise
      encoderDirection = false; // Set direction to counterclockwise
    }
  }
  lastInterruptTime = interruptTime; // Update last interrupt time
}

