import typing as t
from enum import Enum, auto
from dig_ass_critic_protos.client import CriticClient
from dig_ass_text_protos.client import TextClient as DoctorClient

from ..master import logger, config

def call_with_input(func: t.Callable, state):
    def inner(user_replica, chat):
        return func(user_replica, chat), state
    return inner

def message_with_input(message):
    def inner(user_replica, chat):
        return message
    return inner

class States(Enum):
    begin = auto()
    not_medical = auto()
    critical = auto()
    analysis_consultation = auto()
    common_consultation = auto()
    info_collention = auto()
    make_diagnosis = auto()

class TransitionError(Exception):
    """Exception raised for errors in the state transition."""
    def __init__(self, state):
        self.state = state
        self.message = f"No valid transition found from the state: {state}"
        super().__init__(self.message)

class FSM:
    def __init__(self) -> None:
        pass

    def make_transition(self, user_replica, chat, transitions, initial_state, default_state=None):
        transitions = [item for sublist in transitions for item in (sublist if isinstance(sublist, list) else [sublist])]
        for tr in transitions:
            is_maked, state = tr(user_replica, chat)
            if is_maked:
                return state

        if default_state is not None:
            return default_state
        else:
            raise TransitionError(initial_state)

class Transitions:
    def __init__(self, config: t.Dict[str, t.Any], addresses: t.Dict[str, str]) -> None:

        self.st = States
        self.conf = config
        self.addresses = addresses

        self.common_consultant = CriticClient(self.addresses['critic_consultant_address'])
        self.stop_info_collection = CriticClient(self.addresses['critic_collector_address'])
        self.medical = CriticClient(self.addresses['critic_med_address'])

        self.analysis = call_with_input(CriticClient(self.addresses['critic_anal_address']), self.st.analysis_consultation)
        self.critical_condition = call_with_input(CriticClient(self.addresses['critic_triage_address']), self.st.critical)
        self.base_transitions = [self.analysis, self.not_medical, self.critical_condition, self.common_consultation]

    def not_medical(self, user_replica, chat):
        return not self.medical(user_replica, chat), self.st.not_medical

    def compute_number_asked_questions(self, chat):
        return sum([r['State'] == self.st.info_collention.name for r in chat['InnerContext']['Replicas']])

    def make_diagnosis(self, user_replica, chat):
        is_stop_info_collection = self.stop_info_collection(user_replica, chat) >= self.conf['score_collector']
        is_question_limit_reached = self.compute_number_asked_questions(chat) >= self.conf['question_limit']
        return (is_stop_info_collection or is_question_limit_reached, self.st.make_diagnosis)

    def is_diagnosis_maked(self, chat):
        return any([r['State'] == self.st.make_diagnosis.name for r in chat['InnerContext']['Replicas']])

    def common_consultation(self, user_replica, chat):
        is_need_common_consultation = self.is_diagnosis_maked(chat) or self.common_consultant(user_replica, chat)
        return (is_need_common_consultation, self.st.common_consultation)

    def info_collection(self, user_replica, chat):
        return (not self.is_diagnosis_maked(chat), self.st.info_collention)

class Actions:
    def __init__(self, messages: t.Dict[str, t.Any], addresses: t.Dict[str, str]) -> None:
        self.st = States
        self.messages = messages
        self.addresses = addresses

        self.common_consultant = DoctorClient(self.addresses['doctor_consultant_address'])
        self.collector = DoctorClient(self.addresses['doctor_collector_address'])
        self.analysis_consultant = DoctorClient(self.addresses['doctor_anal_address'])
        self.diagnost = DoctorClient(self.addresses['doctor_diagnost_address'])
        self.not_medical_answer = message_with_input(self.messages['non_med_answers'][0])
        self.absurd_answer = message_with_input(self.messages['absurd'])

    def critical_answer(self, user_replica, chat):
        is_previous_state_critical = chat['InnerContext']['Replicas'][-1]['State'] == self.st.critical.name
        if is_previous_state_critical:
            return self.messages['final_triage']
        else:
            return [self.collector(user_replica, chat), self.messages['susp_triage']]

    def make_diagnosis(self, user_replica, chat):
        return [self.diagnost(user_replica, chat), self.messages['final_diag']]

class StateMashine(FSM):
    def __init__(self, config: t.Dict[str, t.Any]) -> None:
        self.conf = config
        self.tr = Transitions(self.conf['constants'], self.conf['addresses'])
        self.act = Actions(self.conf['messages'], self.conf['addresses'])
        self.st = States

    @property
    def state_action_map(self):
        return {
            self.st.analysis_consultation: self.act.analysis_consultant,
            self.st.not_medical: self.act.not_medical_answer,
            self.st.critical: self.act.critical_answer,
            self.st.info_collention: self.act.collector,
            self.st.make_diagnosis: self.act.make_diagnosis,
            self.st.common_consultation: self.act.common_consultant,
        }

    @property
    def state_transition_map(self):
        return {
            self.st.begin: [self.tr.base_transitions, self.tr.info_collection],
            self.st.info_collention: [self.tr.base_transitions, self.tr.make_diagnosis, self.tr.info_collection],
            self.st.make_diagnosis: [self.tr.base_transitions],
            self.st.analysis_consultation: [self.tr.base_transitions, self.tr.info_collection],
            self.st.not_medical: [self.tr.base_transitions, self.tr.info_collection],
            self.st.critical: [self.tr.base_transitions, self.tr.info_collection],
            self.st.common_consultation: [self.tr.base_transitions],
        }

    def __call__(self, user_replica, chat) -> t.Any:

        replicas = chat['InnerContext']['Replicas']
        if len(replicas) > 0:
            initial_state = self.st[replicas[-1]['State']]
        else:
            initial_state = self.st.begin

        transitions = self.state_transition_map[initial_state]
        oper_state = self.make_transition(user_replica, chat, transitions, initial_state, self.st.common_consultation)
        oper_action = self.state_action_map[oper_state]
        return oper_state, oper_action

class SecondOpinion:
    def __init__(self) -> None:
        super().__init__()
        self.fsm = StateMashine(config)
        logger.info(f'SecondOpinion track loaded')

    def __call__(self, user_replica, chat):
        state, action = self.fsm(user_replica, chat)
        susp_context = state == self.fsm.st.not_medical
        state = state.name
        response = action(user_replica, chat)
        if isinstance(response, list):
            response = '\n\n'.join(response)
        return response, susp_context, state

