    api_version: Optional[str] = field(kw_only=True, default=None)


class VisiologyAPI:
    def __init__(self, config: APIConfig):
        self.config = config

    def _get_auth_token(self) -> None:
        """Получает токен аутентификации и сохраняет его в конфигурации."""
        headers = {
            'Content-Type': 'application/x-www-form-urlencoded',
            'Authorization': f"Basic {self.config.basic_auth}"
        }
        data = {
            'grant_type': 'password',
            'scope': 'openid profile email roles viqube_api viqubeadmin_api core_logic_facade dashboards_export_service script_service migration_service_api data_collection',  # noqa
            'response_type': 'id_token token',
            'username': self.config.username,
            'password': self.config.password
        }
        response = requests.post(f'{self.config.base_url}/idsrv/connect/token',
                                 headers=headers, data=urlencode(data), verify=False)
        response.raise_for_status()
        self.config.token = response.json()['access_token']

