import json
import platformdirs

from pathlib import Path
from slugify import slugify


class ConfigurationStorage(dict):
    @staticmethod
    def _acquires_file_pointer(mode):
        def wrap(method):
            def replacement(self, *args, **kwargs):
                try:
                    with open(self.path, mode=mode) as configuration_file:
                        return method(self, configuration_file, *args, **kwargs)
                except OSError as e:
                    raise e
                except Exception as e:
                    raise OSError from e
            return replacement
        return wrap

    def __init__(self, game, category):
        super().__init__()
        self.path = (Path(platformdirs.user_config_dir(slugify(game))) / slugify(category)).with_suffix('.json')
        self.reload()

    @ConfigurationStorage._acquires_file_pointer('r')
    def reload(self, file_pointer):
        self.clear()
        try:
            self.update(json.load(file_pointer))
        except FileNotFoundError:
            pass

    @ConfigurationStorage._acquires_file_pointer('w')
    def save(self, file_pointer):
        self.path.parent.mkdir(parents=True, exist_ok=True)
        json.dump(self, file_pointer, indent=2)
