  [RequestType.Backsub]: config.backsubQueueUrl,
};

export const handleAiRequest = async (req: Request, res: Response) => {
  const { error } = schema.validate(req.body);

  if (error) {
    logger.error('Request validation failed:', error);
    return res.status(400).json({ error: 'Invalid request type' });
  }

  const AiRequest = req.body as AiRequest;
  const { requestType } = AiRequest;

  if (!sqsClient) {
    return res.status(503).json({ status: 'initializing', message: 'SQS clients are still initializing' });
  }

  try {
