      case RequestType.Image:
      case RequestType.Text:
      case RequestType.Backsub:
        const url = reqQueueMap[requestType];
        await sqsClient.sendMessage(url, JSON.stringify(AiRequest));
        break;
      default:
        return res.status(400).json({ error: 'Invalid request type' });
    }

