public class MatchActivity extends AppCompatActivity {
    private static final int ROWS = 5;
    private static final int COLS = 4;
    private static final int BUTTON_COUNT = ROWS * COLS;
    private static final int[] COLORS = {Color.BLUE,Color.GREEN,Color.YELLOW,Color.RED};
    private Button[] buttons = new Button(BUTTON_COUNT);
    private int[] colors = new int[BUTTON_COUNT];
    private TextView scoreTextView;
    private int score = 0;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_match3);

        scoreTextView = findViewById(R.id.Score);
        GridLayout gridLayout = findViewById(R.id.gridLayout);
        gridLayout.setRowCount(ROWS);
        gridLayout.setColumnCount(COLS);

        initializeButtons(gridLayout);
        assignRandomColors();
        setButtonColors();

        Button restartButton = findViewById(R.id.reset_game);
        restartButton.setOnClickListener(v -> restartGame());
    }

    private void initializeButtons(GridLayout gridLayout){
        for (int i = 0; i < BUTTON_COUNT; i++){
            Button button = new Button(this);
            button.setId(i);
            button.setOnClickListener(this::onButtonClick);
            buttons[i] = button;
            gridLayout.addView(button, new GridLayout.LayoutParams(GridLayout.spec(i/COLS)),
                    GridLayout.spec(i%COLS));
        }
    }

    private void assignRandomColors(){
        Random random = new Random();
        for (int i=0; i<BUTTON_COUNT; i++){
            colors[i]=COLORS[random.nextInt(COLORS.length)];
        }
    }

    private void setButtonColors(){
        for (int i=0; i<BUTTON_COUNT; i++){
            buttons[i].setBackgroundColor(colors[i]);
        }
    }

    private void onButtonClick(View view){
        int id = view.getId();
    }

    private void restartGame(){
        score = 0;
        scoreTextView.setText("Score: 0");
        assignRandomColors();
        setButtonColors();
    }
}

