    // Calculate the aspect ratio of the mask
    float maskAspectRatio = maskSize.x / maskSize.y;

    // Adjust the mask position to account for aspect ratio
    vec2 adjustedMaskPosition = maskPosition;
    if (maskAspectRatio > 1.0) {
        adjustedMaskPosition.x -= (maskAspectRatio - 1.0) * maskPosition.y;
    } else {
        adjustedMaskPosition.y -= (1.0 - maskAspectRatio) * maskPosition.x;
    }

    // Calculate the mask delta with adjusted position
    vec2 maskDelta = abs(uv - adjustedMaskPosition) - vec2(maskWidth, maskHeight) / 2.0;
    float mask = step(maskDelta.x, 0.0) * step(maskDelta.y, 0.0);


    uv2 = fract(uv2*3.);

    uv2 = (uv2 - adjustedMaskPosition) / vec2(maskWidth, maskHeight) * 0.5 + 0.5;

    vec3 tex1 = texture2D(u_texture_1, uv).rgb;
    vec3 tex2 = texture2D(u_texture_1, uv2).rgb;


    vec3 col = mix(tex1, tex2, mask);

    gl_FragColor = vec4(col, 1.0);
