using System;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UIElements;

public class JourneyUit : MonoBehaviour
{
    private class ItemData
    {
        public string Title { get; }
        public string Description { get; }
        public System.Action Action { get; }

        public ItemData(string title, string description, System.Action action)
        {
            Title = title;
            Description = description;
            Action = action;
        }
    }


    private ListView listView;
    private List<ItemData> items = new List<ItemData>();

    private void OnEnable()
    {
        var root = GetComponent<UIDocument>().rootVisualElement;
        listView = root.Q<ListView>("listview-journey");

        listView.makeItem = MakeItem;
        listView.bindItem = BindItem;
    }

    public void AddJourney(string title, string desc, Action action)
    {
        var itemData = new ItemData(title, desc, action);
        items.Add(itemData);
        listView.itemsSource = items;
        listView.Rebuild();
    }

    private VisualElement MakeItem()
    {
        var container = new VisualElement();
        container.style.flexDirection = FlexDirection.Row;
        container.style.alignItems = Align.Center;

        var titleLabel = new Label();
        titleLabel.style.flexGrow = 1;
        titleLabel.style.marginLeft = 10;
        titleLabel.style.color = Color.white;
        titleLabel.style.fontSize = 9;

        var descriptionLabel = new Label();
        descriptionLabel.style.flexGrow = 2;
        descriptionLabel.style.marginLeft = 10;
        descriptionLabel.style.color = Color.white;
        descriptionLabel.style.fontSize = 9;

        var actionButton = new Button();
        actionButton.style.marginLeft = 10;

        container.Add(titleLabel);
        container.Add(descriptionLabel);
        container.Add(actionButton);

        return container;
    }

    private void BindItem(VisualElement element, int index)
    {
        var itemData = items[index];
        // Find Labels by their index in the container
        var titleLabel = element.ElementAt(0) as Label; 
        var descriptionLabel = element.ElementAt(1) as Label;
        var actionButton = element.Q<Button>();

        titleLabel.text = itemData.Title;
        descriptionLabel.text = itemData.Description;
        actionButton.text = "회상";
        actionButton.clicked += itemData.Action;
    }    
}


