int input(int *a) {
    if (scanf("%d", a) != 1) {
        printf("n/a");
        return 1;
    }


    if (*a <= 0 || *a > ARRAY_SIZE){
        printf("n/a");
        return 1;
    }


    for (int i = 1; i < ARRAY_SIZE; i++) {
        if (scanf("%d", &a[i]) != 1) {
            printf("n/a");
            return 1;
        }
    }


    char s;
    if( scanf("%c", &s) == 1 && s != '\n'){
        printf("n/a");
        return 1;
    }
    return 0;
}


void output(int *a){
    for(int i = 0; i < ARRAY_SIZE; i++){
        printf("%d ", a[i]);
    }
}


void sort(int *a){
    for(int i = 0; i < ARRAY_SIZE - 1; i++){
        for(int j = 0; j < ARRAY_SIZE - i - 1; j++){
            if(a[j] > a[j + 1]){
                int obmen = a[j];
                a[j] = a[j + 1];
                a[j + 1] = obmen;
            }
        }
    }
}



