            const calendarDiv = document.getElementById('calendar');
            const firstDay = new Date(year, month).getDay();
            const lastDate = new Date(year, month + 1, 0).getDate();
            const prevMonthLastDate = new Date(year, month, 0).getDate();
            const today = new Date();
            const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

            let table = '<table>';
            table += '<tr>';
            daysOfWeek.forEach(day => table += `<th>${day}</th>`);
            table += '</tr><tr>';

            // Fill in the blanks for the previous month
            for (let i = 0; i < firstDay; i++) {
                table += `<td class="not-current">${prevMonthLastDate - firstDay + i + 1}</td>`;
            }

            // Current month's dates
            for (let date = 1; date <= lastDate; date++) {
                if ((firstDay + date - 1) % 7 === 0 && date !== 1) {
                    table += '</tr><tr>';
                }
                const isToday = (date === today.getDate() && month === today.getMonth() && year === today.getFullYear());
                table += `<td class="${isToday ? 'today' : ''}">${date}</td>`;
            }

            // Fill in the blanks for the next month
            const remainingCells = (7 - ((lastDate + firstDay) % 7)) % 7;
            for (let i = 1; i <= remainingCells; i++) {
                table += `<td class="not-current">${i}</td>`;
            }

            table += '</tr></table>';
            calendarDiv.innerHTML = table;
        }

        const today = new Date();
        generateCalendar(today.getFullYear(), today.getMonth());
    </script>

