use crossbeam::queue::ArrayQueue;
use std::sync::Arc;
use std::time::Instant;

const MAX_CLIENTS: usize = 10;
const QUEUE_SIZE: usize = 10_000_000;
const TOTAL_REQUESTS: usize = MAX_CLIENTS * QUEUE_SIZE;

#[derive(Clone, Debug)]
pub struct ClientRequest<T>(T);

lazy_static::lazy_static! {
    static ref CLIENT_QUEUES: ClientQueues<f64> = ClientQueues::new();
}

pub struct ClientQueues<T> {
    queues: [Arc<ArrayQueue<ClientRequest<T>>>; MAX_CLIENTS],
}

impl<T> ClientQueues<T> {
    pub fn new() -> Self {
        let queues = std::array::from_fn(|_| Arc::new(ArrayQueue::new(QUEUE_SIZE)));
        ClientQueues { queues }
    }

    #[inline]
    pub fn add_request(&self, client_id: usize, request: ClientRequest<T>) -> Result<(), ClientRequest<T>> {
        self.queues[client_id].push(request)
    }

    #[inline]
    pub fn get_next_request(&self, client_id: usize) -> Option<ClientRequest<T>> {
        self.queues.get(client_id)?.pop()
    }
}

fn main() {
    let start_time = Instant::now();

    for i in 0..TOTAL_REQUESTS {
        let client_id = i % MAX_CLIENTS;
        let request = ClientRequest(0.0);
        match CLIENT_QUEUES.add_request(client_id, request) {
            Ok(_) => {}
            Err(_) => panic!("Queue overflow"),
        }
        match CLIENT_QUEUES.get_next_request(client_id) {
            Some(_) => {}
            None => panic!("None"),
        }
    }

    let duration = start_time.elapsed();
    let throughput = TOTAL_REQUESTS as f64 / duration.as_secs_f64();

    println!("Total requests: {}", TOTAL_REQUESTS);
    println!("Total time: {:?}", duration);
    println!("Throughput: {:.2} M requests/sec", throughput / 1_000_000.0);
}
