package net.dodian.uber.comm;

import net.dodian.uber.game.Constants;
import net.dodian.uber.game.model.YellSystem;
import net.dodian.uber.game.model.entity.player.Client;

import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.ClosedChannelException;
import java.nio.channels.SocketChannel;
import java.util.Queue;
import java.util.concurrent.ConcurrentLinkedQueue;
import java.util.concurrent.atomic.AtomicBoolean;

public class SocketHandler implements Runnable {

    private final Client player;
    private final SocketChannel socketChannel;
    private final AtomicBoolean processRunning = new AtomicBoolean(true);

    private final Queue<PacketData> myPackets = new ConcurrentLinkedQueue<>();
    private final Queue<ByteBuffer> outData = new ConcurrentLinkedQueue<>();

    private ByteBuffer inputBuffer;
    private final ByteBuffer outputBuffer;

    private final Object outputBufferLock = new Object();
    private final Object processRunningLock = new Object();

    public SocketHandler(Client player, SocketChannel socketChannel) {
        this.player = player;
        this.socketChannel = socketChannel;
        this.inputBuffer = ByteBuffer.allocate(8192);
        this.outputBuffer = ByteBuffer.allocate(8192);
    }

    @Override
    public void run() {
        while (isProcessRunning() && isConnected()) {
            writeOutput();
            parsePackets();
            player.timeOutCounter = 0;
        }
        cleanup();
    }

    private boolean isConnected() {
        try {
            return socketChannel != null && socketChannel.isOpen() && socketChannel.getRemoteAddress() != null && !player.disconnected;
        } catch (IOException e) {
            return false;
        }
    }

    private boolean isProcessRunning() {
        synchronized (processRunningLock) {
            return processRunning.get();
        }
    }

    public void write(byte[] data) {
        synchronized (outputBufferLock) {
            queueOutput(data);
        }
    }

    public void logout() {
        synchronized (processRunningLock) {
            if (processRunning.getAndSet(false)) {
                player.disconnected = true;
                flushOutput();
                cleanup();
                YellSystem.alertStaff(player.getPlayerName() + " has logged out correctly!");
            }
        }
    }

    private void cleanup() {
        try {
            if (socketChannel != null && socketChannel.isOpen()) {
                socketChannel.close();
            }
        } catch (IOException e) {
            System.out.println("SocketHandler: Error during cleanup: " + e.getMessage());
        } finally {
            if (inputBuffer != null) {
                inputBuffer.clear();
            }
            if (outputBuffer != null) {
                synchronized (outputBufferLock) {
                    outputBuffer.clear();
                }
            }
        }
    }

    private void parsePackets() {
        try {
            while (socketChannel.read(inputBuffer) > 0) {
                inputBuffer.flip();

                while (inputBuffer.hasRemaining()) {
                    if (player.packetType == -1) {
                        if (inputBuffer.remaining() < 1) break;
                        player.packetType = inputBuffer.get() & 0xff;
                        if (player.inStreamDecryption != null) {
                            player.packetType = player.packetType - player.inStreamDecryption.getNextKey() & 0xff;
                        } else {
                            player.packetType = player.packetType & 0xff;
                        }

                        if (player.packetType < 0 || player.packetType >= Constants.PACKET_SIZES.length) {
                            throw new IOException("Invalid packet type: " + player.packetType);
                        }

                        player.packetSize = Constants.PACKET_SIZES[player.packetType];

                        if (player.packetSize == 0) {
                            handlePacket(new byte[0], player.packetType);
                            player.packetType = -1;
                            continue;
                        } else if (player.packetSize == -1) {
                            if (inputBuffer.remaining() < 1) break;
                            player.packetSize = inputBuffer.get() & 0xff;
                        } else if (player.packetSize == -2) {
                            if (inputBuffer.remaining() < 2) break;
                            player.packetSize = inputBuffer.getShort() & 0xffff;
                        }
                    }

                    if (inputBuffer.remaining() < player.packetSize) break;

                    byte[] data = new byte[player.packetSize];
                    inputBuffer.get(data);
                    handlePacket(data, player.packetType);
                    player.packetType = -1;
                }

                inputBuffer.compact();
            }

            if (socketChannel.read(inputBuffer) == -1) {
                throw new ClosedChannelException();
            }
        } catch (IOException e) {
            player.disconnected = true;
            synchronized (processRunningLock) {
                processRunning.set(false);
            }
            System.out.println("SocketHandler: Error in parsePackets: " + e.getMessage());
        }
    }

    private void handlePacket(byte[] data, int packetType) {
        PacketData pData = new PacketData(packetType, data, data.length);
        myPackets.add(pData);
        player.timeOutCounter = 0;
    }

    public Queue<PacketData> getPackets() {
        return myPackets;
    }

    public void queueOutput(byte[] copy) {
        ByteBuffer buffer = ByteBuffer.wrap(copy);
        synchronized (outputBufferLock) {
            outData.add(buffer);
        }
    }

    public void writeOutput() {
        try {
            synchronized (outputBufferLock) {
                while (!outData.isEmpty()) {
                    ByteBuffer buffer = outData.poll();
                    if (buffer != null) {
                        while (buffer.hasRemaining()) {
                            socketChannel.write(buffer);
                        }
                    }
                }
            }
        } catch (IOException e) {
            player.disconnected = true;
            System.out.println("SocketHandler: Failed to write output: " + e.getMessage());
        }
    }

    private void flushOutput() {
        try {
            synchronized (outputBufferLock) {
                while (!outData.isEmpty()) {
                    ByteBuffer buffer = outData.poll();
                    if (buffer != null) {
                        while (buffer.hasRemaining()) {
                            socketChannel.write(buffer);
                        }
                    }
                }
            }
        } catch (IOException e) {
            System.out.println("SocketHandler: Failed to flush output: " + e.getMessage());
        }
    }
