{
    char quoteChar = line[startIndex];
    int endIndex = startIndex + 1;
    StringBuilder valueBuilder = new StringBuilder();

    while (endIndex < line.Length)
    {
        if (line[endIndex] == quoteChar && (endIndex == startIndex + 1 || line[endIndex - 1] != '\\'))
        {
            endIndex++; 
