                <h2>Set Up Your Room</h2>
                <div>
                    <label htmlFor="name">Room Name: </label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={room.name}
                        onChange={handleChange}
                        required
                        placeholder="e.g., Living Room"
                    />
                </div>
                <div>
                    <label htmlFor="width">Width (m): </label>
                    <input
                        type="number"
                        id="width"
                        name="width"
                        value={room.layout.width}
                        onChange={handleChange}
                        required
                        step="0.1"
                        min="0.1"
                    />
                </div>
                <div>
                    <label htmlFor="depth">Depth (m): </label>
                    <input
                        type="number"
                        id="depth"
                        name="depth"
                        value={room.layout.depth}
                        onChange={handleChange}
                        required
                        step="0.1"
                        min="0.1"
                    />
                </div>
                <div>
                    <label htmlFor="height">Height (m): </label>
                    <input
                        type="number"
                        id="height"
                        name="height"
                        value={room.layout.height}
                        onChange={handleChange}
                        required
                        step="0.1"
                        min="0.1"
                    />
                </div>

                <button type="submit">{room.id ? 'Update Room' : 'Create Room'}</button>

                {room.id && (
                    <button
                        type="button"
                        onClick={openModal}
                        style={{ backgroundColor: 'red', color: 'white', marginTop: '10px' }}
                    >
                        Delete Room
                    </button>
                )}
            </form>

            {isModalVisible && (
                <div
                    style={{
                        position: 'fixed',
                        top: '0',
                        left: '0',
                        width: '100%',
                        height: '100%',
                        backgroundColor: 'rgba(0, 0, 0, 0.5)',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        zIndex: '9999'
                    }}
                >
                    <div
                        style={{
                            backgroundColor: 'white',
                            padding: '20px',
                            borderRadius: '5px',
                            textAlign: 'center',
                        }}
                    >
                        <h3>Are you sure you want to delete this room?</h3>
                        <button
                            onClick={() => {
                                closeModal();
                            }}
                            style={{ marginRight: '10px', backgroundColor: 'red', color: 'white' }}
                        >
