def send_news(message):
    parts = message.text.split()
    if len(parts) not in [3, 4]:
        bot.reply_to(message, "Использование: /news table_name [N | date]\nГде N - количество новостей, date - в формате d-m-y")
        return

    table_name = f"news_{parts[1]}"

    if len(parts) == 3:
        if parts[2].isdigit():
            try:
                N = int(parts[2])
            except ValueError:
                bot.reply_to(message, "N должно быть целым числом")
                return
            date_filter = None
        else:
            try:
                date_filter = datetime.strptime(parts[2], '%d-%m-%y')
            except ValueError:
                bot.reply_to(message, "Неверный формат даты. Используйте d-m-y")
                return
            N = None
    else:
        bot.reply_to(message, "Неверное количество параметров")
        return

    run_command(f"python collect.py -q '{parts[1]}'")    

