    params = []
    if date_filter:
        start_date = date_filter.timestamp()
        end_date = (date_filter + timedelta(days=1)).timestamp()
        query += " WHERE pub_date >= ? AND pub_date < ?"
        params.extend([start_date, end_date])
    else:
        query += " ORDER BY pub_date DESC"
        if N:
            query += " LIMIT ?"
            params.append(N)

    cursor.execute(query, params)

