using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class Movement : MonoBehaviour {


public float moveSpeed = 5f;
public float rotateSpeed = 20f;
public float rapidDescentSpeed = 10f;
public float pivotOffset = 0.5f; // 旋转轴心点相对于潜水艇中心的偏移比例

private Rigidbody rb;
private bool isRapidDescending = false;

void Start()
{
    rb = GetComponent<Rigidbody>();
    rb.useGravity = false; // 禁用重力
    rb.drag = 0.5f; // 添加一些阻力，模拟水的阻力
}

void FixedUpdate()
{
    if (isRapidDescending)
    {
        RapidDescend();
    }
}

public void Move(float forwardInput, float rightInput)
{
    // 使用 -transform.forward 作为前进方向
    Vector3 movement = (-transform.forward * forwardInput + transform.right * rightInput).normalized;
    rb.MovePosition(rb.position + movement * moveSpeed * Time.deltaTime);
}

public void Rotate(float rotationAmount)
{
    // 计算旋转
    float rotationAngle = rotationAmount * rotateSpeed * Time.deltaTime;
    Quaternion rotation = Quaternion.Euler(0f, rotationAngle, 0f);

    // 计算旋转轴心点（潜水艇前端）
    Vector3 pivotPoint = transform.position - transform.forward * (transform.localScale.z * pivotOffset);

    // 计算潜水艇相对于旋转轴心的位置
    Vector3 pivotToSubmarine = transform.position - pivotPoint;

    // 旋转这个向量
    Vector3 rotatedOffset = rotation * pivotToSubmarine;

    // 计算新的位置
    Vector3 newPosition = pivotPoint + rotatedOffset;

    // 应用新的位置和旋转
    rb.MovePosition(newPosition);
    rb.MoveRotation(rb.rotation * rotation);
}

public void MoveVertical(float direction)
{
    rb.MovePosition(rb.position + transform.up * direction * moveSpeed * Time.deltaTime);
}

public void StartRapidDescent()
{
    isRapidDescending = true;
}

public void StopRapidDescent()
{
    isRapidDescending = false;
}

private void RapidDescend()
{
    rb.MovePosition(rb.position + Vector3.down * rapidDescentSpeed * Time.deltaTime);
}
}
