public static List<Token> ParseLine(string line)
{
    List<Token> result = new List<Token>();
    int startIndex = 0;
    while (startIndex < line.Length)
    {
        while (startIndex < line.Length && char.IsWhiteSpace(line[startIndex]))
        {
            startIndex++;
        }
        if (startIndex >= line.Length)
            break;
        Token token = line[startIndex] == '"' || line[startIndex] == '\''
            ? ReadQuotedField(line, startIndex)
            : ReadField(line, startIndex);
        result.Add(token);
        startIndex = token.GetIndexNextToToken();
    }
    return result;
}

private static Token ReadQuotedField(string line, int startIndex)
{
    char quoteChar = line[startIndex];
    int endIndex = startIndex + 1;
    StringBuilder valueBuilder = new StringBuilder();

    while (endIndex < line.Length)
    {
        if (line[endIndex] == quoteChar && (endIndex == startIndex + 1 || line[endIndex - 1] != '\\'))
        {
            endIndex++; 
            break;
        }
        if (line[endIndex] == '\\' && endIndex + 1 < line.Length)
        {
            endIndex++;
        }
        valueBuilder.Append(line[endIndex]);
        endIndex++;
    }
    return new Token(valueBuilder.ToString(), startIndex, endIndex - startIndex);
}

private static Token ReadField(string line, int startIndex)
{
    int endIndex = startIndex;
    while (endIndex < line.Length && !char.IsWhiteSpace(line[endIndex]) 
        && line[endIndex] != '"' && line[endIndex] != '\'')
    {
        endIndex++;
    }
    return new Token(line.Substring(startIndex, endIndex - startIndex), startIndex, endIndex - startIndex);
}
