  const [gpsData, setGpsData] = useState([]);

  useEffect(() => {
    // Simulated NMEA data fetch
    fetchNMEAData().then(data => setGpsData(data));
  }, []);

  const transformCoordinates = (lat, lon) => {
    const [gcjLon, gcjLat] = wgs2gcj(lat, lon);
    return [gcjLat, gcjLon];
  };  

  const getColor = (speed) => {
    const minSpeed = 0;
    const maxSpeed = Math.max(...gpsData.map(point => point.speed));
    const normalizedSpeed = (speed - minSpeed) / (maxSpeed - minSpeed);
    const hue = 270 - normalizedSpeed * 120; // 270 (purple) to 150 (deep green)
    return `hsl(${hue}, 100%, 50%)`;
  };

  const renderGPSTrace = () => {
    if (gpsData.length === 0) return null;

    const segments = gpsData.map((point, index) => {
      if (index === 0) return null;
      const prevPoint = gpsData[index - 1];
      const coordinates = [
        transformCoordinates(prevPoint.lat, prevPoint.lon),
        transformCoordinates(point.lat, point.lon),
      ];
      const color = getColor(point.speed);
      return (
        <Polyline
          key={index}
          positions={coordinates}
          color={color}
          weight={3}
        >
          <Tooltip permanent>
            Speed: {point.speed.toFixed(2)} km/h<br />
            Direction: {point.direction.toFixed(2)}°
          </Tooltip>
        </Polyline>
      );
    }).filter(Boolean);

    return segments;
  };

  const center = gpsData.length > 0
    ? transformCoordinates(gpsData[0].lat, gpsData[0].lon) 
    : [39.9042, 116.4074]; // Beijing coordinates as default

