void removePEHeaders(const char* filename) {
    ifstream file(filename, ios::binary);
    if (!file.is_open()) {
        cout << "Error opening file." << endl;
        return;
    }

    // Determine file size
    file.seekg(0, ios::end);
    streampos fileSize = file.tellg();
    file.seekg(0, ios::beg);

    // Read PE header (starts at offset 0x3C)
    file.seekg(0x3C, ios::beg);
    uint32_t peHeaderOffset;
    file.read(reinterpret_cast<char*>(&peHeaderOffset), sizeof(uint32_t));

    // Navigate to PE header
    file.seekg(peHeaderOffset, ios::beg);

    // Read PE header
    PEHeader peHeader;
    file.read(reinterpret_cast<char*>(&peHeader), sizeof(PEHeader));

    // Check PE signature
    if (peHeader.signature != 0x4550) { // "PE\0\0"
        cout << "File is not a PE executable." << endl;
        return;
    }

    // Calculate PE header size
    int peHeaderSize = sizeof(PEHeader) + peHeader.sizeOfOptionalHeader;

    // Create buffer to store the main part of the file
    vector<char> buffer(static_cast<size_t>(fileSize - static_cast<streampos>(peHeaderSize)));
    file.seekg(peHeaderSize, ios::beg);
    file.read(buffer.data(), buffer.size());
    file.close();

