        int n = nums2.size();
        int imin = 0, imax = m, half_len = (m + n + 1) / 2;
        double max_of_left, min_of_right;

        while (imin <= imax) {
            int i = (imin + imax) / 2;
            int j = half_len - i;

            if (i < m && nums1[i] < nums2[j - 1]) {
                imin = i + 1;
            } else if (i > 0 && nums1[i - 1] > nums2[j]) {
                imax = i - 1;
            } else {
