#include "Point.h"

using namespace std;

Point::Point(const vector<double>& axes, bool steiner) : axes(axes), steiner(steiner) {}

size_t Point::hash() const {
    return std::hash<uintptr_t>{}(reinterpret_cast<uintptr_t>(this));
}

bool Point::equals(const Point& p) const {
    return axes == p.axes;
}

Point Point::fromLine(const string& line) {
    vector<double> axesVec;
    istringstream iss(line);
    double axis;
    while (iss >> axis) {
        axesVec.push_back(axis);
    }
    return Point(axesVec);
}

void Point::setAxes(const vector<double>& newAxes) {
    axes = newAxes;
}
const void* Point::getAddress() const {
    // Trả về địa chỉ của đối tượng Point
    return static_cast<const void*>(this);
}

bool Point::operator<(const Point& other) const {
    // So sánh các đối tượng Point bằng địa chỉ
    return getAddress() < other.getAddress();
}

const vector<double>& Point::getAxes() const {
    return axes;
}

bool Point::isSteiner() const {
    return steiner;
}

double distance(const Point& A, const Point& B) {
    double sum = 0;
    const auto& axesA = A.getAxes();
    const auto& axesB = B.getAxes();
    for (size_t i = 0; i < axesA.size(); ++i) {
        sum += (axesA[i] - axesB[i]) * (axesA[i] - axesB[i]);
    }
    return std::sqrt(sum);
}

double dotProduct(const Point& A, const Point& B, const Point& C) {
    double result = 0;
    const auto& axesA = A.getAxes();
    const auto& axesB = B.getAxes();
    const auto& axesC = C.getAxes();
    for (size_t i = 0; i < axesA.size(); ++i) {
        result += (axesB[i] - axesA[i]) * (axesC[i] - axesB[i]);
    }
    return result;
}

vector<double> subtract(const vector<double>& vec1, const vector<double>& vec2) {
    vector<double> result(vec1.size());
    for (size_t i = 0; i < vec1.size(); ++i) {
        result[i] = vec1[i] - vec2[i];
    }
    return result;
}

vector<double> normalize(const vector<double>& vec) {
    double norm = 0;
    for (double v : vec) {
        norm += v * v;
    }
    norm = std::sqrt(norm);
    vector<double> result(vec.size());
    for (size_t i = 0; i < vec.size(); ++i) {
        result[i] = vec[i] / norm;
    }
    return result;
}

double clamp(double value, double min, double max) {
    if (value < min) return min;
    if (value > max) return max;
    return value;
}

double angle(const Point& A, const Point& B, const Point& C) {
    const auto& axesA = A.getAxes();
    const auto& axesB = B.getAxes();
    const auto& axesC = C.getAxes();
    if (axesA == axesB || axesA == axesC || axesB == axesC) {
        return M_PI;
    }
    vector<double> normBA = normalize(subtract(axesB, axesA));
    vector<double> normBC = normalize(subtract(axesC, axesB));
    double dot = 0;
    for (size_t i = 0; i < normBA.size(); ++i) {
        dot += normBA[i] * normBC[i];
    }
    return acos(clamp(dot, -1.0, 1.0));
}

vector<double> centerOfGravity(const vector<Point>& points) {
    vector<double> sumAxes(points[0].getAxes().size(), 0.0);
    for (const auto& p : points) {
        const auto& axes = p.getAxes();
        for (size_t i = 0; i < axes.size(); ++i) {
            sumAxes[i] += axes[i];
        }
    }
    for (double& v : sumAxes) {
        v /= points.size();
    }
    return sumAxes;
}

vector<double> fermatPoint(const vector<Point>& points) {
    if (points.size() != 3) {
        return {};
    }

    const Point& A = points[0];
    const Point& B = points[1];
    const Point& C = points[2];

    double angleA = angle(B, A, C);
    if (angleA >= 2 * M_PI / 3) {
        return A.getAxes();
    }
    double angleB = angle(A, B, C);
    if (angleB >= 2 * M_PI / 3) {
        return B.getAxes();
    }
    double angleC = angle(A, C, B);
    if (angleC >= 2 * M_PI / 3) {
        return C.getAxes();
    }

    double a = distance(B, C);
    double b = distance(A, C);
    double c = distance(A, B);

    double x = 1 / sin(angleA + M_PI / 3);
    double y = 1 / sin(angleB + M_PI / 3);
    double z = 1 / sin(angleC + M_PI / 3);

    double _D = a * x + b * y + c * z;

    vector<double> result(A.getAxes().size());
    const auto& axesA = A.getAxes();
    const auto& axesB = B.getAxes();
    const auto& axesC = C.getAxes();
    for (size_t i = 0; i < result.size(); ++i) {
        result[i] = (a * x / _D) * axesA[i] + (b * y / _D) * axesB[i] + (c * z / _D) * axesC[i];
    }
    return result;
}

