# 定义计算亚组发病率的函数
calculate_subgroup_rates <- function(df, group_var, event_var) {
    if (group_var == "") {
        # 计算指定分组变量的亚组结果
        subgroup_results <- df %>%
            group_by(GROUP) %>%
            summarize(
                Subgroup_Person_days = sum(follow_up_days, na.rm = TRUE),
                Subgroup_Events = sum(!!sym(event_var), na.rm = TRUE),
                Crude_Incidence_Rate = sum(!!sym(event_var), na.rm = TRUE) * 1e6 / sum(follow_up_days, na.rm = TRUE),
                CI_Lower = max(0, Crude_Incidence_Rate - qnorm(0.975) * sqrt(Crude_Incidence_Rate / Subgroup_Person_days * 1e6)),
                CI_Upper = Crude_Incidence_Rate + qnorm(0.975) * sqrt(Crude_Incidence_Rate / Subgroup_Person_days * 1e6), .groups = "drop"
            ) %>%
            mutate(event = event_var) %>%
            as.data.frame()
    } else {
        # group_var <- paste0(group_var, ", GROUP")
        subgroup_results <- df %>%
            group_by(!!sym(group_var), GROUP) %>%
            summarize(
                Subgroup_Person_days = sum(follow_up_days, na.rm = TRUE),
                Subgroup_Events = sum(!!sym(event_var), na.rm = TRUE),
                Crude_Incidence_Rate = sum(!!sym(event_var), na.rm = TRUE) * 1e6 / sum(follow_up_days, na.rm = TRUE),
                CI_Lower = max(0, Crude_Incidence_Rate - qnorm(0.975) * sqrt(Crude_Incidence_Rate / Subgroup_Person_days * 1e6)),
                CI_Upper = Crude_Incidence_Rate + qnorm(0.975) * sqrt(Crude_Incidence_Rate / Subgroup_Person_days * 1e6), .groups = "drop"
            ) %>%
            mutate(event = event_var) %>%
            as.data.frame()
    }

    if (group_var == "") {
        subgroup_results$sub_GROUP_value <- "overall"
    } else {
        subgroup_results$sub_GROUP <- group_var
        nms <- names(subgroup_results)
        names(subgroup_results)[which(nms == group_var)] <- "sub_GROUP_value"
        subgroup_results$sub_GROUP_value <- as.character(subgroup_results$sub_GROUP_value)
    }

    return(subgroup_results)
}

