@Component({
  selector: 'app-logical-link-dialog',
  templateUrl: './logical-link-dialog.component.html',
  styleUrl: './logical-link-dialog.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class LogicalLinkDialogComponent
  extends DialogComponent<LogicalLinkDialogData>
  implements AfterViewInit, OnDestroy
{
  @ViewChild('tableCanvas', { static: true }) protected readonly tableCanvas!: TableCanvasComponent;
  @ViewChildren('tableTreeContainer') tableContainers!: QueryList<ElementRef>;
  private readonly resizeObservers: ResizeObserver[] = [];
  protected readonly links$ = new BehaviorSubject<TableLinkModel[]>([]);

  protected readonly displayedColumns: string[] = ['name'];
  protected readonly form: FormGroup = new FormGroup({});
  protected readonly dataTypeTemplate = this.data.dataTypeTemplates;
  protected readonly sourceDoType: DOTypeDto | undefined;
  protected readonly targetDoType: DOTypeDto | undefined;
  protected readonly sourceLn: LogicalNodeDto = this.data.sourceLn;
  protected readonly targetLn: LogicalNodeDto = this.data.targetLn;
  protected readonly portEvent$ = new Subject<PortEvent>();
  private readonly destroy$ = new Subject<void>();

  transformer = (node: DoTypeExt, level: number): DoTypeExtNode => {
    return {
      expandable: !!node.children && node.children.length > 0,
      node: node,
      level: level
    };
  };

  sourceTreeControl = new FlatTreeControl<DoTypeExtNode>(
    node => node.level,
    node => node.expandable
  );

  targetTreeControl = new FlatTreeControl<DoTypeExtNode>(
    node => node.level,
    node => node.expandable
  );

  treeFlattener = new MatTreeFlattener(
    this.transformer,
    node => node.level,
    node => node.expandable,
    node => node.children
  );

  sourceTableTree = new MatTreeFlatDataSource<DoTypeExt, DoTypeExtNode, DoTypeExtNode>(
    this.sourceTreeControl,
    this.treeFlattener
  );
  targetTableTree = new MatTreeFlatDataSource<DoTypeExt, DoTypeExtNode, DoTypeExtNode>(
    this.targetTreeControl,
    this.treeFlattener
  );

  constructor(dialogRef: DialogRef<LogicalLinkDialogData>, @Inject(DIALOG_DATA) data: LogicalLinkDialogData) {
    super(dialogRef, data);

    if (this.dataTypeTemplate) {
      this.sourceDoType = this.findDoType(this.data.sourcePort.doTypeId);
      this.targetDoType = this.findDoType(this.data.targetPort.doTypeId);

      this.sourceTableTree.data = this.buildDoTypeExtension(
        this.data.sourcePort,
        this.data.sourceLn,
        TableTreePortType.SOURCE
      );
      this.openFirstNode(this.sourceTreeControl);
      this.targetTableTree.data = this.buildDoTypeExtension(
        this.data.targetPort,
        this.data.targetLn,
        TableTreePortType.TARGET
      );
      this.openFirstNode(this.targetTreeControl);
      this.createLinksFromInternalData();
    } else {
      throw new Error('Cannot find DataTypeTemplate');
    }
  }

  ngAfterViewInit(): void {
    this.tableContainers.forEach(container => {
      const mutationObserver = new ResizeObserver(event => {
        if (event.length > 0) {
          const target = event[0].target as HTMLElement;

          if (target.id == 'source') {
            this.updatePorts(this.sourceTableTree.data);
          } else if ((target.id = 'target')) {
            this.updatePorts(this.targetTableTree.data);
          }
        }
      });

      mutationObserver.observe(container.nativeElement);

      this.resizeObservers.push(mutationObserver);
    });

    this.links$.pipe(takeUntil(this.destroy$)).subscribe(links => {
      links.forEach(link => {
        this.subscribeToPortToggle(link.sourcePort$.value, link);
        this.subscribeToPortToggle(link.targetPort$.value, link);
      });
    });
  }

  override onSubmit(): void {
    this.data.internalLinks = this.setDataFromConnectionsTable();
    super.onSubmit();
  }

  private createLinksFromInternalData(): void {
    this.links$.next(
      this.data.internalLinks
        .map(link => {
          const sourcePort = this.getSourcePort(link.source);
          const targetPort = this.getTargetPort(link.target);

          if (sourcePort && targetPort) {
            this.openRelatedNodes(sourcePort, targetPort);

            return new TableLinkModel(
              new BehaviorSubject<TablePortModel>(sourcePort),
              new BehaviorSubject<TablePortModel>(targetPort)
            );
          } else {
            console.error(`Cannot find ports for link: ${link.source} -> ${link.target}`);

            return null;
          }
        })
        .filter(link => link !== null)
    );
  }

  private getSourcePort(id: string): TablePortModel | undefined {
    return this.findPortInTree(this.sourceTableTree.data, id);
  }

  private getTargetPort(id: string): TablePortModel | undefined {
    return this.findPortInTree(this.targetTableTree.data, id);
  }

  private findPortInTree(doTypeExts: DoTypeExt[], id: string): TablePortModel | undefined {
    for (const doTypeExt of doTypeExts) {
      if (doTypeExt.port && doTypeExt.id === id) {
        return doTypeExt.port;
      }

      if (doTypeExt.children.length > 0) {
        const port = this.findPortInTree(doTypeExt.children, id);

        if (port) {
          return port;
        }
      }
    }

    return undefined;
  }

  private openRelatedNodes(sourcePort: TablePortModel, targetPort: TablePortModel): void {
    const sourceNode = this.sourceTreeControl.dataNodes.find(node => node.node.id === sourcePort.id);
    const targetNode = this.targetTreeControl.dataNodes.find(node => node.node.id === targetPort.id);

    if (sourceNode) {
      const sourceParentNode = this.findParentNode(sourceNode, this.sourceTreeControl);

      if (sourceParentNode) {
        this.openNodeAndParents(sourceParentNode, this.sourceTreeControl);
      }
    }

    if (targetNode) {
      const targetParentNode = this.findParentNode(targetNode, this.targetTreeControl);

      if (targetParentNode) {
        this.openNodeAndParents(targetParentNode, this.targetTreeControl);
      }
    }
  }

  private openNodeAndParents(node: DoTypeExtNode, treeControl: FlatTreeControl<DoTypeExtNode>): void {
    if (node) {
      treeControl.expand(node);
      const parentNode = this.findParentNode(node, treeControl);

      if (parentNode) {
        this.openNodeAndParents(parentNode, treeControl);
      }
    }
  }

  private findParentNode(node: DoTypeExtNode, treeControl: FlatTreeControl<DoTypeExtNode>): DoTypeExtNode | undefined {
    const nodeIndex = treeControl.dataNodes.indexOf(node);

    if (nodeIndex > 0) {
      for (let i = nodeIndex - 1; i >= 0; i--) {
        if (treeControl.getLevel(treeControl.dataNodes[i]) < treeControl.getLevel(node)) {
          return treeControl.dataNodes[i];
        }
      }
    }

    return undefined;
  }

  private subscribeToPortToggle(port: TablePortModel, link: TableLinkModel): void {
    port.toggle$.pipe(takeUntil(this.destroy$)).subscribe(hidden => {
      if (hidden) {
        this.handlePortHidden(port, link);
      } else {
        this.handlePortVisible(port, link);
      }
    });

    if (port.parent) {
      this.subscribeToPortToggle(port.parent, link);
    }
  }

  private handlePortHidden(port: TablePortModel, link: TableLinkModel): void {
    const openParent = this.findOpenParent(port);

    if (openParent) {
      if ((link.sourceParentPort$.value && link.sourceParentPort$.value === port) || link.sourcePort$.value === port) {
        link.sourceParentPort$.next(openParent);
      } else if (
        (link.targetParentPort$.value && link.targetParentPort$.value === port) ||
        port === link.targetPort$.value
      ) {
        link.targetParentPort$.next(openParent);
      }
    }
  }

  private handlePortVisible(_port: TablePortModel, link: TableLinkModel): void {
    if (!link.sourcePort$.value.toggle$.value) {
      link.sourceParentPort$.next(undefined);
    } else {
      const openParent = this.findOpenParent(link.sourcePort$.value);

      link.sourceParentPort$.next(openParent);
    }

    if (!link.targetPort$.value.toggle$.value) {
      link.targetParentPort$.next(undefined);
    } else {
      const openParent = this.findOpenParent(link.targetPort$.value);

      link.targetParentPort$.next(openParent);
    }
  }

  private findOpenParent(port: TablePortModel): TablePortModel | undefined {
    if (!port.parent) {
      return undefined;
    }

    if (!port.parent.toggle$.value) {
      return port.parent;
    }

    return this.findOpenParent(port.parent);
  }

  protected setPortEvent(event: DragEvent, type: 'drop' | 'end' | 'over' | 'start', port?: TablePortModel): void {
    if (type == 'start') {
      if (event.dataTransfer) {
        event.dataTransfer.setDragImage(new Image(), 0, 0);
      }
    } else if (type == 'over') {
      event.preventDefault();
    }

    this.portEvent$.next({
      type: type,
      event: event,
      port: port
    });
  }

  protected onScrollEvent(treeControl: FlatTreeControl<DoTypeExtNode, DoTypeExtNode>): void {
    if (treeControl == this.sourceTreeControl) {
      this.updatePorts(this.sourceTableTree.data);
    } else if (treeControl == this.targetTreeControl) {
      this.updatePorts(this.targetTableTree.data);
    }
  }

  protected updatePorts(doTypeExt: DoTypeExt[]): void {
    doTypeExt.forEach(doType => {
      doType.port?.update$.next();

      if (doType.children.length > 0) {
        this.updatePorts(doType.children);
      }
    });
  }

  private findDoType(doTypeId: string): DOTypeDto | undefined {
    return Object.values(this.dataTypeTemplate!.doType).find((doType: DOTypeDto) => doType.id === doTypeId);
  }

  private buildDoTypeExtension(
    doBounded: DOBoundedDto,
    ln: LogicalNodeDto,
    tableTreePortType: TableTreePortType
  ): DoTypeExt[] {
    return DoTypeExtensionBuilder.instance(doBounded, ln, this.dataTypeTemplate!, tableTreePortType).build();
  }

  protected deleteConnection(link: TableLinkModel): void {
    this.links$.next(this.links$.value.filter(l => l !== link));
  }

  protected deleteConnections(): void {
    this.links$.next([]);
  }

  protected setDialogWindowHeader(): string {
    return `Редактор связей между:
    ${this.sourceLn.prefix
      ?.concat(this.sourceLn.lnClass[0].concat(this.sourceLn.inst))
      .concat('.')
      .concat(this.data.sourcePort.name)}
       -
    ${this.targetLn.prefix
      ?.concat(this.targetLn.lnClass[0].concat(this.targetLn.inst))
      .concat('.')
      .concat(this.data.targetPort.name)}`;
  }

  private openFirstNode(tree: FlatTreeControl<DoTypeExtNode>): void {
    tree.expand(tree.dataNodes[0]);
  }

  protected onTableSelect(event: MouseEvent, link: TableLinkModel): void {
    if (event.shiftKey) {
      link.toggleSelection();
    } else {
      this.links$.value.forEach(link => link.selected$.next(false));
      link.selected$.next(true);
    }
  }

  private setDataFromConnectionsTable(): InternalAttributeLinkDto[] {
    return this.links$.value.map(link => ({
      source: link.sourcePort$.getValue().id,
      target: link.targetPort$.getValue().id
    }));
  }

  ngOnDestroy(): void {
    this.resizeObservers.forEach(o => o.disconnect());
  }
}

export class TablePortComponent implements AfterViewInit, OnDestroy {
  @ViewChild('portDiv', { static: true }) readonly portDiv!: ElementRef;
  @Input({ required: true }) port!: TablePortModel;
  @Input({ required: true }) canvas!: TableCanvasModel;

  private readonly destroy$ = new Subject<void>();
  protected readonly TableTreePortType = TableTreePortType;

  ngAfterViewInit(): void {
    this.port.coords$.next(this.getCenter());

    this.port.update$.pipe(takeUntil(this.destroy$)).subscribe(() => {
      this.port.coords$.next(this.getCenter());
    });
    this.port.toggle$.next(false);

    this.port.links$
      .pipe(
        takeUntil(this.destroy$),
        map(links => {
          if (this.port.portType.value === TableTreePortType.SOURCE) {
            return links.length === 0;
          } else {
            return this.checkActiveBasedOnAncestors(links.length === 0);
          }
        })
      )
      .subscribe(active => {
        this.port.active$.next(active);
      });

    if (this.port.portType.value === TableTreePortType.TARGET) {
      this.port.active$.pipe(takeUntil(this.destroy$)).subscribe(active => {
        this._setChildrenActive(active, this.port.children$.value);
      });
    }

    this.port.active$
      .pipe(
        //TODO костыль. Вынужденная мера, т.к. Angular не обновляет через шаблон и async
        takeUntil(this.destroy$)
      )
      .subscribe(isActive => {
        this.portDiv.nativeElement.style.backgroundColor = isActive ? '' : '#5f5f5f';
        this.portDiv.nativeElement.style.pointerEvents = isActive ? '' : 'none';
      });
  }

  private checkActiveBasedOnAncestors(isLinksEmpty: boolean): boolean {
    let ancestor = this.port.parent;

    while (ancestor) {
      if (!ancestor.active$.getValue()) {
        return false;
      }

      ancestor = ancestor.parent;
    }

    return isLinksEmpty;
  }

  private _setChildrenActive(active: boolean, children: TablePortModel[]): void {
    children.forEach(child => {
      child.active$.next(active);

      if (child.children$.value.length > 0) {
        this._setChildrenActive(active, child.children$.value);
      }
    });
  }

  getCenter(): PortCoords {
    const canvasRect = this.canvas.htmlElement.getBoundingClientRect();
    const rect = (this.portDiv.nativeElement as HTMLElement).getBoundingClientRect();

    return {
      x: rect.left + rect.width / 2 - canvasRect.left,
      y: rect.top + rect.height / 2 - canvasRect.top
    };
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.port.toggle$.next(true);
  }
}

import { ChangeDetectionStrategy, Component, ElementRef, Input, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { Subject, Subscription } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { TablePortModel } from '../port/table-port-model';
import { TableLinkModel } from './table-link-model';
import { PortCoords } from '../logical-link-interfaces';

@Component({
  selector: 'app-table-link',
  templateUrl: './table-link.component.html',
  styleUrl: './table-link.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TableLinkComponent implements OnInit, OnDestroy {
  @Input({ required: true }) link!: TableLinkModel;
  @ViewChild('path', { static: true }) protected readonly path!: ElementRef<SVGPathElement>;
  private readonly destroy$ = new Subject<void>();

  private sourcePort?: TablePortModel;
  private targetPort?: TablePortModel;

  private sourceCoords: PortCoords = { x: 0, y: 0 };
  private targetCoords: PortCoords = { x: 0, y: 0 };

  private srcCoordsSubscription?: Subscription;
  private trgCoordsSubscription?: Subscription;

  ngOnInit(): void {
    this.link.sourcePort$.pipe(takeUntil(this.destroy$)).subscribe(port => {
      if (this.sourcePort) {
        this.sourcePort.removeLinkModel(this.link);
      }

      this.sourcePort = port;

      const links = port.links$.value;

      links.push(this.link);

      port.links$.next(links);

      this.subscribeForSource(port);
    });
    this.link.targetPort$.pipe(takeUntil(this.destroy$)).subscribe(port => {
      if (this.targetPort) {
        this.targetPort.removeLinkModel(this.link);
      }

      this.targetPort = port;

      const links = port.links$.value;

      links.push(this.link);

      port.links$.next(links);

      this.subscribeForTarget(port);
    });
    this.link.sourceParentPort$
      .pipe(takeUntil(this.destroy$))
      .subscribe(port => this.subscribeForSource(port ?? this.link.sourcePort$.value));
    this.link.targetParentPort$
      .pipe(takeUntil(this.destroy$))
      .subscribe(port => this.subscribeForTarget(port ?? this.link.targetPort$.value));
  }

  protected updatePath(sourceCoords?: PortCoords, targetCoords?: PortCoords): void {
    const start = sourceCoords ?? this.sourceCoords;
    const end = targetCoords ?? this.targetCoords;
    const mX = (start.x + end.x) / 2;

    this.sourceCoords = { ...start };
    this.targetCoords = { ...end };

    this.path.nativeElement.setAttribute(
      'd',
      `M${start.x} ${start.y} C ${mX} ${start.y}, ${mX} ${end.y}, ${end.x} ${end.y}`
    );
  }

  private subscribeForSource(port?: TablePortModel): void {
    if (this.srcCoordsSubscription) {
      this.srcCoordsSubscription.unsubscribe();
      this.srcCoordsSubscription = undefined;
    }

    if (port) {
      this.srcCoordsSubscription = port.coords$.subscribe((coords: PortCoords) => this.updatePath(coords, undefined));
    }
  }

  private subscribeForTarget(port?: TablePortModel): void {
    if (this.trgCoordsSubscription) {
      this.trgCoordsSubscription.unsubscribe();
      this.trgCoordsSubscription = undefined;
    }

    if (port) {
      this.trgCoordsSubscription = port.coords$.subscribe((coords: PortCoords) => this.updatePath(undefined, coords));
    }
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.subscribeForSource(undefined);
    this.subscribeForTarget(undefined);

    this.sourcePort?.removeLinkModel(this.link);
    this.targetPort?.removeLinkModel(this.link);
  }
}

@Component({
